import React from 'react';
import axios from 'axios';
import Board from './board.js';

class BoardsList extends React.Component {
	render() {
		return 	<div>
							<h1> Boards </h1>

						</div>
	}
}

class App extends React.Component {
	state = {boards: []}

	getBoards = async () => {
    const resp = await axios.get(`/get_boards`)
    const boardsList = resp.data['boards']
    console.log(resp)
    const resp2 = await axios.get('/get_tasks')
    console.log(resp2)
    this.setState({boards: [boardsList]});
  };

  componentDidMount() {
    this.getBoards();
  }

	render() {
		if (this.state.boards === [])
			return <div>loading</div>

		return <BoardsList boards={this.state.boards}/>

	}
}


export default App;
