import React from 'react';
import axios from 'axios';
import pencil from './pencil.svg'
import up_arrow from './up_arrow.png'

const Button = (props) => {
	return 	<button className={props.styling} onClick={props.action}>{props.body}</button>
}

class RaisePriority extends React.Component {
	render () {
		return 	<img alt="raise-priority" onClick = {this.props.changePriority} className='priority-button raise-priority' src={up_arrow}></img>
	}
}

class DropPriority extends React.Component {
	render () {
		return  <img alt="drop-priority" onClick={this.props.changePriority} className='priority-button drop-priority' src={up_arrow}></img>
	}
}

class Task extends React.Component {
	state = {id: this.props.id,
	 		title: this.props.title, 
	 		status:this.props.status, 
	 		priority: this.props.priority,
	 		theme: this.props.theme,
	 		epic: this.props.epic,
	 		timeboxes: this.props.timeboxes,
	 		editing:false}

	updateStatus = async (new_status) => {
		const prev_status = this.state.status
		await axios.post('/update_task_status', {
		  id: this.state.id,
		  from_status: prev_status,
		  to_status: new_status
		});
		await this.props.updateStatus(this.state.id, new_status)
		this.setState(this.state)

	}

	edit = () => {
		this.setState({editing: true})
	}

	save = (event) => {
		this.setState({editing: false})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: this.state.status,
		  theme: this.state.theme,
		  epic: this.state.epic,
		  timebox: this.state.timebox,
		  priority: this.state.priority
		});
	}

	editTaskName = (event) => {
		event.preventDefault()
		this.setState({editing: false})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: this.state.status,
		  theme: this.state.theme,
		  priority: this.state.priority
		});
	}

	changePriority = (direction) => {
		this.props.changePriority(this.state.id, direction)
	}

	convertToEpic =() => {
		this.props.convertToEpic(this.state.id)
	}

	cta = () => {
		if (this.state.status === 'To Do') {
			return <Button action={() => this.updateStatus('In Progress')} body='Start' styling='btn btn-success' />
		} else if (this.state.status === 'In Progress') {
			return <Button action={() => this.updateStatus('Done')} body='Done' styling='btn btn-primary'/>
		} else if (this.state.status === 'Done') {
			return 	<Button action={() => this.updateStatus('To Do')} body='Reopen' styling='btn btn-secondary'/>

		}

	}

	render() {
		return 	<div draggable="true" onDragStart = {() => console.log('Dragging')}className='card m-2'>
					{this.state.editing === false ? (
						<div className='card-body'>
							<div className='row'>
								<div className='col-2'>
									<div className='theme-blob' style={{backgroundColor: this.props.theme_color}}></div>
								</div>
								<div className='col-8 text-center'>
									<div className='status badge bg-secondary mx-3' style={{backgroundColor: this.props.epic_color}}>{this.state.epic}</div>
								</div>
								<div className='col-2'>
									<img alt="edit-pencil" className='edit-pencil' onClick={this.edit} src={pencil}></img>
								</div>
							</div>
							<div className='row'>
								<div className='col-10'>
									<div className='card-title mt-3'>
										{this.state.title}
									</div>
								</div>
								<div className='col-2'>
									{this.props.priority !== 0 ? (
										<RaisePriority changePriority={() => this.changePriority('Up')}/>) 
										: (
								<div></div>
							)}
								</div>
							</div>
							<div className='row'>
								<div className='col-6'>
									<div className='timebox'>
										{this.props.timeboxes}
									</div>
								</div>
								<div className='col-4'>
									{this.cta()}
								</div>
								<div className='col-2'>
									{this.props.last === false ? (
									<DropPriority changePriority={() => this.changePriority('Down')}/>
									)	: (
									<div></div>
									)}
								</div>
							</div>
						</div> ) 

						:(

						<div className='card-body'>
							<div className='row'>
								<div className='col-6'>							
									<Button action={this.save} body='save' styling='btn btn-primary'/>
								</div>
								<div className='col-6'>
									<button onClick={this.convertToEpic}>Convert to Epic</button>
								</div>
							</div>
							<div className='row'>
								<div className='col-12'>
									<form onSubmit={this.editTaskName}>
								 	<div className='form-group'>
										<textarea
											className='form-control form-control mt-1 mb-1'
								            type="text" 
								            value={this.state.title}
								            onChange={event => this.setState({ title: event.target.value })}
								            placeholder= {this.state.title}
								            required 
								        />
								        <select className="form-select mt-1 mb-1" onChange={event => this.setState({ theme: event.target.value })}>
										  <option defaultValue >{this.state.theme}</option>
										  {this.props.themes.map(theme => {
											return theme.title !== this.state.theme ?
										  	<option key={theme.id} value={theme.title}>{theme.title}</option>
										  	: <></>
										  }
										   )};
										</select>
										<select className="form-select mt-1 mb-1" onChange={event => this.setState({ epic: event.target.value })}>
										  <option defaultValue >{this.state.epic}</option>
										  {this.props.epics.map(epic => {
											return epic.title !== this.state.epic  ?
										  	<option key={epic.id} value={epic.title}>{epic.title}</option>
										  	: <></>
										  }
										   )};
										</select>
										<select className="form-select mt-1 mb-1" onChange={event => this.setState({ timebox: event.target.value })}>
										  <option defaultValue >{this.state.timebox}</option>
										  {this.props.all_timeboxes.map(tb => {
											return tb.title !== this.state.timebox  ?
										  	<option key={tb.id} value={tb.title}>{tb.title}</option>
										  	: <></>
										  }
										   )};
										</select>
									</div>
								 </form>
								</div>
							</div>
						</div> )
						}
					</div>
	}
}

export default Task;
