import React from 'react';
import axios from 'axios';
import deleteIcon from './delete_icon.png';

const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByItem('')}>Clear Filters</div>
}

const MenuItem = (props) => {
	const deleteItem = (category, itemId) => {
		if (category === 'Themes') {
			axios.post('/delete_theme', {
				theme_id: itemId,
				board_id: props.boardId
			}).then( (response) => {
				props.deleteItem('theme', response.data.themes)
			}, (error) => {
				console.log(error)
			})
		} else if (category === 'Epics') {
			axios.post('/delete_epic', {
				epic_id: itemId,
				board_id: props.boardId
			}).then( (response) => {
				props.deleteItem('epic', response.data.epics)
			}, (error) => {
				console.log(error)
			})
		} else {
			axios.post('/delete_timebox', {
				timebox_id: itemId,
				board_id: props.boardId
			}).then( (response) => {
				props.deleteItem('timebox', response.data.timeboxes)
			}, (error) => {
				console.log(error)
			})
		}


	}
	return  <div className='row menu-item'>
				<div onClick = {() => props.filterByItem(props.data.title)} className="col-10">
					<div className='menu-item-blob' style={{backgroundColor: props.data.color}}> </div>
					<div>{props.data.title} </div>
				</div>
				<div className="col-2">
					<img alt="delete-icon" className='delete-icon-sidemenu' onClick={() => deleteItem(props.itemCat, props.data.id)} src={deleteIcon}></img>
				</div>
			</div>
}

class MenuSection extends React.Component {
	state = {filtering: false, expanded: false, display: false}

	addItem = (itemData) => {
  	this.setState(prevState => ({
    	items: [...this.state.items, itemData],
    }));
  };

  	deleteItem = (itemType, remainingItems) => {
		this.setState(prevState => ({
		items: remainingItems,
    }));


  	}


	filter = (itemName) => {
		this.setState({filtering: true})
		this.props.filterByItem(itemName)

	}
	html = (type) => {
		return <div className='card'>
					<div onClick = {() => {this.setState({display: true})}} className='card-header menu-section-header'>{this.props.title}</div>

						<div className='card-body menu-card-body' style={{display: this.state.display === true ? 'block' : 'none' }}>
						{this.state.filtering === true ? <ClearFilters filterByItem={this.filter}/>
						: <div></div> }
					{type}
						{this.state.items.map(item => <MenuItem deleteItem={this.deleteItem} itemCat={this.props.title} boardId={this.props.boardId} key={item.id} data={item} filterByItem={this.filter}/>)}

					</div>

				</div>}

	componentWillMount() {
		this.setState({items: this.props.items})

	}
}

export default Themes


class AddElement extends React.Component {
	state = {title: ''}

	form = (action, title, placeholder) => {
		return <form className='add-element-form' onSubmit={action}>
										 	<div className='form-group element-input '>
												<input
													className='form-control form-control m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder={placeholder}
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}

class AddTheme extends AddElement {
	addTheme = async (event) => {
		event.preventDefault()
		axios.post('/add_theme', {
		  title: this.state.title,
    	  board_id: this.props.boardId
		})
		.then((response) => {
		  this.props.onSubmit(response.data)
		}, (error) => {
		  console.log(error);
		});
		this.setState({title: ''})

	}
	render() {
		return this.form(this.addTheme, this.state.title, 'Add New Theme')
	}
}

class Themes extends MenuSection {
	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	render() {
		return this.html(<AddTheme boardId={this.props.boardId} onSubmit={this.addItem}/>)
	}			
}
