import React from 'react';
import axios from 'axios';


class AddElement extends React.Component {
	form = (action, title, placeholder, className) => {
		return <form className={className} onSubmit={action}>
										 	<div className='form-group'>
												<input
													className='form-control form-control-sm m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder={placeholder}
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}

const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByStatus('')}>clear</div>
}

class Theme extends React.Component {
	render() {
		return <div className='theme-button' onClick = {() => this.props.filterByStatus(this.props.themeTitle)} style={{backgroundColor: this.props.color}}>{this.props.themeTitle}</div>
	}
}


class AddTheme extends AddElement {
	state = {title: ''}
	addTheme = async (event) => {
		await axios.post('/add_theme', {
		  title: this.state.title
		});
	}
	render() {
		return this.form(this.addTheme, this.state.title, 'Add New Theme', 'add-theme-form')
	}
}

class Themes extends React.Component {
	state = {filtering: false}
	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}
	render() {
		return <div className='row'>
								{this.props.themes !== [] ?  (
								 <div className='col-12'>
									{this.props.themes.map(theme => 
										<Theme key={theme.id} themeTitle={theme.title} color={theme.color} filterByStatus={this.filterByStatus}/>)}
										{this.state.filtering === true ? (
											<ClearFilters filterByStatus={this.filterByStatus}/>) : <div></div> }
										<AddTheme />
								 </div> )
								: <div></div> }						 	
					 </div>
	}				
}

class AddEpic extends AddElement {
	state = {title: ''}

	addEpic = () => {
		axios.post('/add_epic', {
			title: this.state.title
		})

	}

	render() {
		return this.form(this.addEpic, this.state.title, 'Add New epic', 'add-epic-form')
	}
}

class Epic extends React.Component {
	render() {
		return <div className='epic-button' onClick = {() => this.props.filterByEpic(this.props.epicTitle)} style={{Color: this.props.color, border: `4px solid ${this.props.color}`}}><div className='epic-blob' style={{backgroundColor: this.props.color}}> </div>{this.props.epicTitle}</div>
	}
}




class MenuSection extends React.Component {
	state = {filtering: false, expanded: false}
	render() {
		return 	<div className='card menu-item'>
					<div className='card-header'>{this.props.title}</div>
				</div>
	}
}

class Timeboxes extends MenuSection {
}

class Epics extends MenuSection {
	state = {filtering: false}
	filterByEpic = (epic) => {
		this.props.filterByEpic(epic)
		if (epic === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}
	//render() {
		return /*<div className='col-12'>
						{this.props.epics.map(epic => 
							<Epic key={epic.id} epicTitle={epic.title} color={epic.color} filterByEpic={this.filterByEpic}/>)}
							<AddEpic />
					 </div> */					 	
	//}					
}

class SideMenu extends React.Component {
	render() {
		return 	<div className='col-3'>
					<div className='accordion'>
						<Themes title='Themes' themes={this.props.themes} filterByStatus={this.props.filterByStatus} />
						<Epics title='Epics' epics={this.props.epics} filterByEpic={this.props.filterByEpic} />
						<Timeboxes title='Timeboxes' timeboxes={this.props.timeboxes} />
					</div>
				</div>
	}
}



export default SideMenu;
