import React from 'react';
import axios from 'axios';
import deleteIcon from './delete_icon.png';



class AddElement extends React.Component {
	state = {title: ''}

	form = (action, title, placeholder) => {
		return <form className='add-element-form' onSubmit={action}>
										 	<div className='form-group element-input '>
												<input
													className='form-control form-control m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder={placeholder}
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}




class AddTheme extends AddElement {
	addTheme = async (event) => {
		event.preventDefault()
		axios.post('/add_theme', {
		  title: this.state.title,
    	  board_id: this.props.board_id
		})
		.then((response) => {
		  this.props.onSubmit(response.data)
		}, (error) => {
		  console.log(error);
		});
		this.setState({title: ''})

	}
	render() {
		return this.form(this.addTheme, this.state.title, 'Add New Theme')
	}
}

class AddEpic extends AddElement {
	addEpic = () => {
		axios.post('/add_epic', {
			title: this.state.title,
    	    board_id: this.props.board_id
		})
	}
	render() {
		return this.form(this.addEpic, this.state.title, 'Add New epic')
	}
}

class AddTimebox extends AddElement {
	addTimebox = () => {
		axios.post('/add_timebox', {
			title: this.state.title,
    	    board_id: this.props.board_id
		})
	}
	render() {
		return this.form(this.addTimebox, this.state.title, 'Add New Timebox')
	}
}

const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByItem('')}>Clear Filters</div>
}

const MenuItem = (props) => {
	const deleteItem = (category, itemId) => {
		if (category === 'Themes') {
			axios.post('/delete_theme', {
				theme_id: itemId,
				board_id: props.boardId
			})
		} else if (category === 'Epics') {
			axios.post('/delete_epic', {
				epic_id: itemId,
				board_id: props.boardId
			})
		} else {
			axios.post('/delete_timebox', {
				timebox_id: itemId,
				board_id: props.boardId
			})
		}


	}
	return  <div onClick = {() => props.filterByItem(props.data.title)} className='menu-item'>
				<div className='menu-item-blob' style={{backgroundColor: props.data.color}}> </div>
				<div>{props.data.title} <img alt="delete-icon" className='delete-icon' onClick={() => deleteItem(props.itemCat, props.data.id)} src={deleteIcon}></img></div>
			</div>
}

class MenuSection extends React.Component {
	state = {filtering: false, expanded: false, display: false, items: this.props.items}

	addItem = (itemData) => {
  	this.setState(prevState => ({
    	theme: [...this.state.items, itemData],
    }));
  };
	filter = (itemName) => {
		this.setState({filtering: true})
		this.props.filterByItem(itemName)

	}
	html = (type) => {
		return <div className='card'>
					<div onClick = {() => {this.setState({display: true})}} className='card-header menu-section-header'>{this.props.title}</div>

						<div className='card-body menu-card-body' style={{display: this.state.display === true ? 'block' : 'none' }}>
						{this.state.filtering === true ? <ClearFilters filterByItem={this.filter}/>
						: <div></div> }
					{type}
						{this.props.items.map(item => <MenuItem itemCat={this.props.title} boardId={this.props.boardId} key={item.id} data={item} filterByItem={this.filter}/>)}

					</div>

				</div>}
}

class Themes extends MenuSection {
	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	render() {
		return this.html(<AddTheme board_id={this.props.board_id} onSubmit={this.addItem}/>)
	}			
}

class Epics extends MenuSection {
	state = {filtering: false}
	filterByEpic = (epic) => {
		this.props.filterByEpic(epic)
		if (epic === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}
	render() {
		return this.html(<AddEpic board_id={this.props.board_id} onSubmit={this.addItem}/>)
	}				
}


class Timeboxes extends MenuSection {
	render() {
		return this.html(<AddTimebox board_id={this.props.board_id} onSubmit={this.addItem}/>)
	}
}

class SideMenu extends React.Component {


	render() {
		return 	<div className='col-3'>
					<div className='accordion'>
						<Themes title='Themes' items={this.props.themes} filterByItem={this.props.filterByStatus} boardId={this.props.board_id}/>
						<Epics title='Epics' items={this.props.epics} filterByItem={this.props.filterByEpic} addItem={this.addEpic} boardId={this.props.board_id}/>
						<Timeboxes title='Timeboxes' items={this.props.timeboxes} filterByItem={this.props.filterByTimebox} addItem={this.addTimebox} boardId={this.props.board_id}/>
					</div>
				</div>
	}
}



export default SideMenu;
