class Header extends React.Component {
	render() {
		return <div className='header'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}


class Task extends React.Component {
	render() {
		return <div className='card w-25 mb-2'>{this.props.title}</div>
	}
}

class TaskList extends React.Component {
	render() {
		return <div>
						<h5>I am the Task List</h5>
						{this.props.tasks.map(task => <Task key={task.id} {...task}/>)}
					 </div>
	}
}						

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
  	event.preventDefault();
    //const resp = await axios.post(`https://api.github.com/users/${this.state.userName}`);
    const data = {title: this.state.title}
    const response = await fetch('/add_task', {
    method: 'POST', // 
    mode: 'cors', // 
    cache: 'no-cache', // 
    credentials: 'same-origin', 
    headers: {
      'Content-Type': 'application/json'
    },
    redirect: 'follow', 
    referrerPolicy: 'no-referrer',
    body: JSON.stringify(data) 
  });
  const resp = response.json(); // parses JSON response into native JavaScript objects

    this.props.onSubmit(resp.data);
    this.setState({ userName: '' });
  };
	render () {
		return <form onSubmit={this.handleSubmit}>
						<input 
		          type="text" 
		          value={this.state.title}
		          onChange={event => this.setState({ title: event.target.value })}
		          placeholder="Task Name" 
		          required 
		        />
						<button className='btn btn-primary'>Add Task</button>
					 </form>
	}
}

class App extends React.Component {
	state = {tasks: []}
	componentDidMount() {
    fetch('/get_tasks')
      .then((response) => response.json())
      .then((data) => this.setState({ tasks: data['tasks'] }));

		};

		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...prevState.tasks, taskData],
    }));
  };
	render() {
		return <div>
						<Header />
						<CreateTaskForm onSubmit={this.addNewTask}/>
						<TaskList tasks={this.state.tasks}/>
					 </div>
	}
}

export default App;
