import React from 'react';
import axios from 'axios';
import deleteIcon from './../img/delete_icon.png';
import { Droppable } from 'react-beautiful-dnd';
import { Modal, Form} from "react-bootstrap";
import './themes.css'

class AddTheme extends React.Component {
	state = {title: ''}

	addTheme = async (event) => {
			event.preventDefault()
		  axios.post('/add_theme', {
		  title: this.state.title,
    	project_id: this.props.projectId
		})
		.then((response) => {
		  this.props.addTheme(response.data)
		}, (error) => {
		  console.log(error);
		});
		this.setState({title: ''})
	}

	handleKeyDown = (event) => {
		event.preventDefault()
		if (event.key === 'Enter') {
			axios.post('/add_theme', {
		  title: this.state.title,
    	project_id: this.props.projectId
		})
		.then((response) => {
		  this.props.addTheme(response.data)
		}, (error) => {
		  console.log(error);
		});
		}
	}

	render() {
						return <div className="col-3" onClick={this.props.showAddThemeModal}>
										<div 
											className='container card theme border border-dark col-12' 
											style={{backgroundColor: 'whitesmoke'}}
										>
											<h4 className='add-theme-title'>Add Theme</h4>
											<h3>+</h3>
										</div>
									</div>
	}
}

const Theme = (props) => {
		const deleteTheme = () => {
		props.deleteTheme(props.data.id)
	}
		const theme = String('Theme:' + props.data.id)

	return  <Droppable droppableId={theme}>
						{(provided) => (
							<div className="col-3" onClick = {() => props.filterByTheme(props.title)}>
								<div 
									ref={provided.innerRef} 
									className='container card theme' 
									style={{backgroundColor: props.data.color}}
								>
									<div  
										className='theme-title'
									>
										<div>
											{props.data.title}
										</div>
									</div>
									<div className='theme-delete-icon'>
										<img 
											alt="delete-icon" 
											className='delete-icon' 
											onClick={() => deleteTheme()} 
											src={deleteIcon}
										>
										</img>
									</div>
									{provided.placeholder}
				  			</div>
				  		</div>
			  		)}
					</Droppable>
}

const ClearFilters = (props) => {
	return <div className="col-3" onClick={props.clearFilters}>
										<div 
											className='container card theme border border-dark col-12' 
											style={{backgroundColor: 'pink'}}
										>
											<h4 className='add-theme-title'>Clear Filters</h4>
											<h3>x</h3>
										</div>
									</div> 
}


class Themes extends React.Component {

	constructor(props) {
    super(props);

    this.state = {
      themes: this.props.themes,
      showAddThemeModalStatus: false
    };
  }

  static getDerivedStateFromProps(nextProps, prevState){
			   if(nextProps.themes !== prevState.themes){
			     return { themes: nextProps.themes};
			  }
			  else return null;
			}

	filterByTheme = (theme) => {
		this.props.filterByTheme(theme);
		if (theme === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	addTheme = async (event) => {
		  event.preventDefault()
			axios.post('/add_theme', {
		  title: this.state.newThemeTitle,
    	project_id: this.props.projectId
		})
		.then((response) => {
		  this.props.addTheme(response.data)
		}, (error) => {
		  console.log(error);
		});
		this.setState({showAddThemeModalStatus: false})
  };

  showAddThemeModal = () => {
  	this.setState({showAddThemeModalStatus: true})
  }

	deleteTheme = (themeId) => {
		this.props.deleteTheme(themeId)
  };

  clearFilters = () => {
  	this.props.clearFilters()
  }



	render() {
		return  <div className='themes-container col-2' id='themes-container'>
							<div className='component-container'>
								<div className='component-title'>
								  <div>Your Themes</div>
								</div>
								<div className='row m-2'>
									{this.state.themes.map(theme => 
										<Theme 
											projectId={this.props.projectId} 
											key={theme.id} data={theme} 
											filterByTheme={this.filterByTheme}
											title={theme.title}
											deleteTheme={this.deleteTheme}
										/>)}
									<AddTheme 
										projectId={this.props.projectId}
										showAddThemeModal={this.showAddThemeModal}
										addTheme={this.addTheme}
									/>
									{this.props.filtering === true && 
										<ClearFilters clearFilters={this.clearFilters} />
									}
							</div>
							<Modal show={this.state.showAddThemeModalStatus}>
					      <Modal.Header onClick= {() => this.setState({showAddThemeModalStatus: false})}closeButton>Add Theme</Modal.Header>
					      <Modal.Body>
					      	<Form onSubmit = {this.addTheme}>
							  <div className="row mb-3">
							    <Form.Group className='col-12' controlId="formTimeboxTitle">
							      <Form.Label>Name</Form.Label>
							      <Form.Control type="text" onChange={event => this.setState({ newThemeTitle: event.target.value })}/>
							    </Form.Group>
							  </div>
							  <input className='btn btn-primary' type="submit" value="Submit" />
							</Form>
					      </Modal.Body>
					    </Modal>
					    </div>
						</div>



	}
}


export default Themes
