import React from 'react';
import axios from 'axios';
import Board from './board.js';

class BoardsList extends React.Component {
	state = {boards: []}
	render() {
		return 	<div>
							<h1> Boards </h1>
							{this.props.boards[0]}
						</div>
	}
}

class App extends React.Component {
	getBoards = async () => {
    const resp = await axios.get(`/get_boards`);
    this.setState({boards: resp['boards']})
  };

	render() {
		if (!this.state.boards) {
			return <div></div>;
		} else {
		return <BoardsList boards={this.state.boards}/>
	}

	}
}


export default App;
