import React from 'react';
import { useHistory } from "react-router-dom";
import 'bootstrap';
import axios from 'axios';
import './planning.css';
import Task from './../Task/Task.js';
import Themes from './../Themes/Themes.js'
import ModeSwitcher from './../ModeSwitcher/ModeSwitcher.js'
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Modal from "react-bootstrap/Modal";


class TaskList extends React.Component {
	render() {
		const tasks = [...this.props.tasks].sort((a,b) => a.priority > b.priority ? 1 : -1)

		return <div className='col-6 mx-auto w-100'>
							{tasks.map((task, index) =>

								<Draggable key={task.id} draggableId={String(task.id)} index={index}>
									{provided => (

										<div  
											ref={provided.innerRef}
											{...provided.draggableProps}
			      					{...provided.dragHandleProps}		
					    			>
											<Task 
												innerRef={provided.innerRef}
												last={task.priority === tasks.length -1 ? true : false}
									 			updateStatus={this.props.updateStatus}
												changePriority={this.props.changePriority}
												themes={this.props.themes}
												epics={this.props.epics}
												allTimeboxes={this.props.allTimeboxes}
												convertToEpic={this.props.convertToEpic}
												deleteTask={this.props.deleteTask}
												{...task}
											/>
										</div>

									)}
								</Draggable>
							)}
					 </div>
				}
			}

class TasksColumn extends React.Component {
	state = {tasks: []}

	render() {
		return <div className='col-4 mt-2'>
							<div className='container'>
								{this.props.colId}
							</div>
							<div className='row mt-2'>
								<Droppable droppableId={this.props.colId}>
									{(provided) => (

										<div
								      ref={provided.innerRef}
								      {...provided.draggableProps}
								      {...provided.dragHandleProps}
								    >
											<TaskList
											innerRef={provided.innerRef}
											{...provided.droppableProps}
											tasks={this.props.tasks}
											changePriority={this.props.changePriority} 
											updateStatus={this.props.updateStatus} 
											themes={this.props.themes} 
											epics={this.props.epics}
											allTimeboxes={this.props.allTimeboxes}
											convertToEpic={this.props.convertToEpic}
											deleteTask={this.props.deleteTask}
											/>
											{provided.placeholder}
										</div>

									)}
								</Droppable>
							</div>
						</div>
					}
				}

const AddTimebox = (props) => {
	return <div className='col-4 mt-5'>
			 <div className='card'>
			 	<div className='card-body mx-auto'>
			 		<div className='btn btn-primary'> Add a Timebox</div>
			 		<p>Timeboxes let you group tasks together into blocks for example
			 			To do today or to do before holiday</p>
			 	</div>
			 </div>
			</div>
}

class TasksContainer extends React.Component {
	render() {
		return  <div className='row'>
				{this.props.allTimeboxes.map(timebox => 
					<TasksColumn 
						colId={timebox.title}
						tasks={this.props.tasks.filter(task => task.timeboxes[0] === timebox.title)}
						changePriority={this.props.changePriority} 
						updateStatus={this.props.updateStatus} 
						themes={this.props.themes} 
						epics={this.props.epics}
						all_timeboxes={this.props.all_timeboxes}
						convertToEpic={this.props.convertToEpic}
						deleteTask={this.props.deleteTask}
					/>
				)}
				{this.props.allTimeboxes.length === 1 &&
			        <AddTimebox />
			      }
			    <Modal show={this.state.showAddTimeboxModal}>
			      <Modal.Header>Hi</Modal.Header>
			      <Modal.Body>asdfasdf</Modal.Body>
			      <Modal.Footer>This is the footer</Modal.Footer>
			    </Modal>
			  </div>

	}
}						


class CreateTaskForm extends React.Component {
	state = { title: ''};

	handleSubmit = async (event) => {
    axios.post('/add_task', {
    	title: this.state.title,
    	project_id: this.props.projectId
    })
    .then((response) => {
		  this.props.onSubmit(response.data)
		}, (error) => {
		  console.log(error);
		});
		  event.preventDefault()
    	this.setState({title : ''})   
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				       </div>
			         <div className='col text-center'>
								<button className='btn btn-primary mx-auto mb-4'>Add Task</button>
							 </div>
						 </form>
						</div>
					 </div>
	}
}

class Planning extends React.Component {
		state = {dataReceived:false}

		addNewTask = (taskData) => {
	  	this.setState(prevState => ({
	    	tasks: [...this.state.tasks, taskData],
	    }));
	  };

  	deleteTask = (taskId) => {
  		axios.post('/delete_task', {
  			task_id: taskId,
  			project_id: this.state.projectId
  		}).then(
  		(response) => {
  			this.setState(prevState => ({
		    	tasks: response.data.tasks,
		    }));
  		})
  	}

  	filterByTheme = async (theme) => {
  			if (theme === '') {
  				this.getTasks(this.state.projectId)
  			} else {
  			await this.getTasks(this.state.projectId)
	  		const filteredTasks = this.state.tasks.filter(task => task.theme === theme);
	  		this.setState({tasks: filteredTasks});
	  		}		
  	}

  	getTasks = async (projectId) => {
    const resp = await axios.get(`/get_tasks?project_id=${projectId}`)
    this.setState({ tasks: resp.data['tasks'], 
    								themes:resp.data['themes'], 
    								timeboxes: resp.data['timeboxes'] 
    							});
  };

   	async componentDidMount() {
   		const sp = new URLSearchParams(this.props.location.query);
		const projectId = sp.get("projectId")
		if (projectId === null) {
			this.props.history.push('/')
		} else{
			await this.getTasks(projectId);
    		this.setState({dataReceived: true, projectId: projectId})
		}

    }

    onDragEnd = (result) => {
  		const {destination, source, draggableId} = result;

  		if (!destination) {
  			return;
  		}

  		if (
  			destination.droppableId === source.droppableId &&
  			destination.index === source.index
  			) {
  			return;
  		}

  		const taskId = Number(draggableId)
  		const obj = this.state.tasks.find(o => o.id === taskId);

  		if (destination.droppableId.slice(0,5) === 'Theme') {
  			const themeId = Number(destination.droppableId.slice(6,11))
  			axios.post('/update_task_theme', {
  			project_id: this.state.projectId,
  			task_id: taskId,
  			theme_id: themeId
  			})

  		const themeObj = this.state.themes.find(o => o.id === themeId)

  		obj.theme = themeObj.title
  		obj.theme_color = themeObj.color

  		} else {

	  		obj.timeboxes = [destination.droppableId]

	  		if (obj.timebox === 'Backlog') {
	  			if (destination.index > source.index) {
	  				obj.priority = destination.index +0.5
	  			} else {
	  				obj.priority = destination.index -0.5
	  			}

	  		} else {obj.priority = -1}

	  		axios.post('/update_task', {
	  			project_id: this.state.projectId,
	  			task_id: taskId,
	  			priority: obj.priority,
	  			timebox: obj.timeboxes[0]
	  		})
	  	}

  		const allTasks = this.state.tasks.filter(task => task !== obj)
  		this.setState(prevState => ({
    	tasks: [...allTasks, obj],
    	}));		
  	}

	render() {
		return this.state.dataReceived === true ? (
						<DragDropContext
							onDragEnd = {this.onDragEnd}
						>
							<div className='row planning-page'>
								<CreateTaskForm onSubmit={this.addNewTask} projectId={this.state.projectId}/>
								<Themes 
									themes={this.state.themes} 
									filterByTheme={this.filterByTheme} 
									projectId={this.state.projectId}
								/>
								<TasksContainer
								 	tasks={this.state.tasks} 
									themes={this.state.themes} 
									allTimeboxes={this.state.timeboxes}
									updateStatus={this.updateStatus} 
									deleteTask={this.deleteTask}
									showAddTimeboxModal={false}
								/>
						 </div>
						</DragDropContext> 
						)
					: <div></div>
	}
}


export default Planning;
