import axios from 'axios';

const prefix = 'http://localhost:6000'

const getRequest = async (endpoint, params) => {
  try {
      const resp = await axios.get(prefix + endpoint + '?' + params)
      return resp
  } catch (err) {
    console.log(err)
  }
}

const postRequest = async (endpoint, params) => {
  try {
      const resp = await axios.get(prefix + endpoint + '?' + params)
      return resp
  } catch (err) {
    console.log(err)
  }
}



export { getRequest, postRequest }
