import {createAuthProvider} from 'react-token-auth';


export function login() {
    createAuthProvider({
        accessTokenKey: 'access_token',
        onUpdateToken: (token) => fetch('/auth/refresh', {
            method: 'POST',
            body: token.access_token
        })
        .then(r => r.json())
    });
}

export function logout() {
    createAuthProvider({
        accessTokenKey: 'access_token',
        onUpdateToken: (token) => fetch('/auth/refresh', {
            method: 'POST',
            body: token.access_token
        })
        .then(r => r.json())
    });
}
