class Task extends React.Component {
	render() {
		return <div className='card w-25 mb-2'>{this.props.title}</div>
	}
}

class TaskList extends React.Component {
	render() {
		return <div>
						<h5>I am the Task List</h5>
						{this.props.tasks.map(task => <Task key={task.id} {...task}/>)}
					 </div>
	}
}						

class CreateTaskForm extends React.Component {
	state = { title: ''};
	render () {
		return <form onSubmit={this.props.addTask}>
						<input 
		          type="text" 
		          value={this.state.title}
		          onChange={event => this.setState({ title: event.target.value })}
		          placeholder="Task Name" 
		          required 
		        />
						<button className='btn btn-primary'>Add Task</button>
					 </form>
	}
}

class Header extends React.Component {
	render() {
		return <div className='header'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

class App extends React.Component {
	state = {tasks: []}
	componentDidMount() {
    fetch('/get_tasks')
      .then((response) => response.json())
      .then((data) => this.setState({ tasks: data['tasks'] }));

		};
	render() {
		handleAddTask = async (event) => {
			event.preventDefault();
			const requestOptions = {
					method: 'POST',
					headers: { 'Content-Type': 'application/json' },
					body: JSON.stringify({ title: this.state.title })
					};
			const response = await fetch('/add_task', requestOptions);
			const data = await response.json();
		};
		const tasks = this.state.tasks
		return <div>
						<Header />
						<CreateTaskForm addTask={handleAddTask}/>
						<TaskList tasks={tasks}/>
					 </div>
	}
}

export default App;
