import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import Task from './task.js';
import SideMenu from './sidemenu.js';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Link } from 'react-router-dom';


class TaskList extends React.Component {
	render() {
		const tasks = [...this.props.tasks].sort((a,b) => a.priority > b.priority ? 1 : -1)
		return <div className='col-6 mx-auto w-100'>
										{tasks.map((task, index) =>
											<Draggable key={task.id} draggableId={String(task.id)} index={index}>
												{provided => ( 
													<div  ref={provided.innerRef}
																{...provided.draggableProps}
								      					{...provided.dragHandleProps}

								    			>
														<Task innerRef={provided.innerRef}

																last={task.priority === tasks.length -1 ? true : false}
													 			updateStatus={this.props.updateStatus}
																changePriority={this.props.changePriority}
																themes={this.props.themes}
																epics={this.props.epics}
																all_timeboxes={this.props.all_timeboxes}
																convertToEpic={this.props.convertToEpic}
																{...task}
																/>
													</div>
													)}
											</Draggable>
														)
										}
						</div>
	}
}

class TasksColumn extends React.Component {
	state = {tasks: []}
	render() {
		return <div className='col-3'>
							<div className='container'>
							{this.props.colId} Tasks
							</div>
							<div className='row mt-2'>
								<Droppable droppableId={this.props.colId}>
									{(provided) => (
										<div
								      ref={provided.innerRef}
								      {...provided.draggableProps}
								      {...provided.dragHandleProps}
								    >
											<TaskList
											innerRef={provided.innerRef}
											{...provided.droppableProps}
											tasks={this.props.tasks}
											changePriority={this.props.changePriority} 
											updateStatus={this.props.updateStatus} 
											themes={this.props.themes} 
											epics={this.props.epics}
											all_timeboxes={this.props.all_timeboxes}
											convertToEpic={this.props.convertToEpic}
											/>
											{provided.placeholder}
										</div>
									)}
								</Droppable>
							</div>
						</div>
	}
}

const TasksContainer = (props) => {
  	const doneOnly = props.tasks.filter(task => task.status === 'Done');
 		const inProgressOnly = props.tasks.filter(task => task.status === 'In Progress'); 
  	const toDoOnly = props.tasks.filter(task => task.status === 'To Do');

		return  <React.Fragment>
						<TasksColumn 
												colId='To Do'
												tasks={toDoOnly}  
												changePriority={props.changePriority} 
												updateStatus={props.updateStatus} 
												themes={props.themes} 
												epics={props.epics}
												all_timeboxes={props.all_timeboxes}
												convertToEpic={props.convertToEpic}
												/>
						<TasksColumn 
												colId='In Progress'
												tasks={inProgressOnly}  
												changePriority={props.changePriority} 
												updateStatus={props.updateStatus} 
												themes={props.themes} 
												epics={props.epics}
												all_timeboxes={props.all_timeboxes}
												convertToEpic={props.convertToEpic}
												/>
						<TasksColumn 
												colId='Done'
												tasks={doneOnly}  
												changePriority={props.changePriority} 
												updateStatus={props.updateStatus} 
												themes={props.themes} 
												epics={props.epics}
												all_timeboxes={props.all_timeboxes}
												convertToEpic={props.convertToEpic}
												/>
						</React.Fragment>	
}

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
		console.log(this.props)
    axios.post('/add_task', {
    	title: this.state.title,
    	board_id: this.props.board_id
    })
    .then((response) => {
		  this.props.onSubmit(response.data)
		}, (error) => {
		  console.log(error);
		});
		    event.preventDefault()
    this.setState({title : ''})   
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto mb-4'>Add Task</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class Header extends React.Component {
	render() {
		return <div className='col-12 header text-center'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						 <Link to={{pathname: '/'}}>Boards</Link>
						</div>

	}
}

class Board extends React.Component {
		state = {data_received:false}

		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...this.state.tasks, taskData],
    }));
  };


  	filterByStatus = async (status) => {
  			if (status === '') {
  				this.getTasks(this.state.board_id)
  			} else {
  			await this.getTasks(this.state.board_id)
	  		const filteredTasks = this.state.tasks.filter(task => task.theme === status);
	  		this.setState({tasks: filteredTasks});
	  		}		
  	}

  	filterByEpic = async (epic) => {
  			if (epic === '') {
  				this.getTasks(this.state.board_id)
  			} else {
  			await this.getTasks(this.state.board_id)
	  		const filteredTasks = this.state.tasks.filter(task => task.epic === epic);
	  		this.setState({tasks: filteredTasks});
	  		}
  	}

  	filterByTimebox = async (timebox) => {
  			if (timebox === '') {
  				this.getTasks(this.state.board_id)
  			} else {
  			await this.getTasks(this.state.board_id)
	  		const filteredTasks = this.state.tasks.filter(task => task.timebox === timebox);
	  		this.setState({tasks: filteredTasks});
	  		}
  	}

  	getTasks = async (board_id) => {
    const resp = await axios.get(`/get_tasks?board_id=${board_id}`, {
    	params: {
    		board: this.state.board
    	}
    });
    await this.setState({ tasks: resp.data['tasks'], themes:resp.data['themes'], epics: resp.data['epics'], timeboxes: resp.data['timeboxes'] });
  };

  	convertToEpic = (task_id) => {
  		const task = this.state.tasks.find(o => o.id === task_id);
  		const tasks = this.state.tasks.filter( t => t !== task)
  		this.setState(prevState => ({
    	tasks: tasks,
    	}));
  		axios.post('/convert_to_epic', {
  			task_id: task_id
  		}).then((response) => {
  			console.log(response)
			  const epic = {id: response['data']['id'], title: response['data']['title'], color: response['data']['color']};
			  this.setState({epics: [...this.state.epics, epic]})
			}, (error) => {
			  console.log(error);
			});
  	}


   	async componentWillMount() {
   		const path = this.props.location.pathname
   		const board_id = Number(path.slice(7,12))
    	await this.getTasks(board_id);
    	this.setState({data_received: true, board_id: board_id})
    }

    onDragEnd = (result) => {
  		const {destination, source, draggableId} = result;

  		if (!destination) {
  			return;
  		}

  		if (
  			destination.droppableId === source.droppableId &&
  			destination.index === source.index
  			) {
  			return;
  		}

  		const task_id = Number(draggableId)
  		const obj = this.state.tasks.find(o => o.id === task_id);
  		obj.status = destination.droppableId

  		if (obj.status === 'To Do') {
  			obj.priority = destination.index + 1
  		} else {obj.priority = -1}

  		axios.post('/update_task', {
  			task_id: task_id,
  			status: obj.status,
  			priority: obj.priority
  		})

  		const all_tasks = this.state.tasks.filter(task => task !== obj)
  		this.setState(prevState => ({
    	tasks: [...all_tasks, obj],
    	}));		
  	}

	render() {
		return this.state.data_received === true ? (
						<DragDropContext
						onDragEnd = {this.onDragEnd}



						>
							<div className='row'>
							<Header />
							<CreateTaskForm onSubmit={this.addNewTask} board_id={this.state.board_id}/>
							<SideMenu epics={this.state.epics}
												themes={this.state.themes}
												timeboxes={this.state.timeboxes}
												filterByStatus={this.filterByStatus} 
												filterByEpic={this.filterByEpic}
												filterByTimebox={this.filterByTimebox}
												/>
							<TasksContainer tasks={this.state.tasks} 
													themes={this.state.themes} 
													epics={this.state.epics}
													all_timeboxes={this.state.timeboxes}
													updateStatus={this.updateStatus} 
													changePriority={this.changePriority}
													convertToEpic={this.convertToEpic}
													/>
							<div className='row'>
							</div>
						 </div>
						</DragDropContext> 
						)
					: <div></div>

	}
}


export default Board;
