import React from 'react'
import { Form } from "react-bootstrap";
import AddItem from './../Forms/AddItem.js'

import './timeboxes.css';

class Timebox extends React.Component {
	render() {
		return  <div className='timebox card'>
					<div className='card-body'>
						<h2 className='card-title text-center'>{this.props.title}</h2>
						<div className='timebox-status'>Status:{this.props.status}</div>
						<div className='timebox-goals mx-auto'>
							{this.props.goals.map( goal =>
								<div className='timebox-goal'>
									{goal}
								</div>
								)}
						</div>
						<div className='timebox-tasks'>
							{this.props.tasks.map( task =>
									task.timebox === this.props.title &&
										<div className='timebox-task'>
											{task.title}
										</div>
									)}
						</div>
					</div>
				</div>
	}
}

class AddTimebox extends AddItem {
	toDoToday = () => {
		axios.post('/timebox_shortcuts', {
			project_id: this.props.projectId,
			shortcut_id: 1
		}).then((response) => {
			this.props.addTimebox(response.data)
		}, (error) => {
		  console.log(error);
		});
	}

	toDoThisWeek = () => {
		axios.post('/timebox_shortcuts', {
			project_id: this.props.projectId,
			shortcut_id: 2
		}).then((response) => {
			this.props.addTimebox(response.data)
		}, (error) => {
		  console.log(error);
		});
	}

	addItem = (event) => {
		event.preventDefault()
		const goals = [this.state.goalOne, this.state.goalTwo, this.state.goalThree]
		axios.post('/add_timebox', {
			project_id: this.props.projectId,
			title: this.state.newTimeboxTitle,
			start_date: this.state.newTimeboxStartDate,
			end_date: this.state.newTimeboxEndDate,
			goals: goals
		}).then((response) => {
			this.props.addTimebox(response.data)
		}, (error) => {
		  console.log(error);
		});
	}

    formGroups =  <div>
    				<Form.Group className='col-10 mx-auto' controlId="formTitle">
					  <Form.Control placeholder="Timebox Name" type="text" onChange={event => this.setState({ newTitle: event.target.value })}/>
					</Form.Group>
					<Form.Group className='col-12 mx-auto mt-2 goals-group' controlId="formTitle">
					  <Form.Control placeholder="Goal 1" type="text" onChange={event => this.setState({ goalOne: event.target.value })}/>
					  <Form.Control placeholder="Goal 2" type="text" onChange={event => this.setState({ goalTwo: event.target.value })}/>
					  <Form.Control placeholder="Goal 3" type="text" onChange={event => this.setState({ goalThree: event.target.value })}/>
					</Form.Group>
				  </div>
	cta = 'Add Timebox'

}

class Timeboxes extends React.Component {
	render() {
		return  <div className='timeboxes-container col-6'>
				  <div className='component-container'>
				    <div className='component-title'>
				  		<span>Timeboxes</span>
				  	</div> 	
				  	<AddTimebox />
				  	{this.props.timeboxes.map(timebox =>
				  		timebox.title !== 'Backlog' &&
				  		<Timebox
							key={timebox.id}
							tasks={this.props.tasks}
							{...timebox}
						 />
				  		)}	
				  </div>
				</div>
	}
}

export default Timeboxes
