import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';


class TaskList extends React.Component {
	render() {		
		return <div className='col-6 mx-auto w-100'>
							{this.props.tasks.map((task, index) =>

								<Draggable key={task.id} draggableId={String(task.id)} index={index}>
									{provided => (

										<div  
											ref={provided.innerRef}
											{...provided.draggableProps}
			      					{...provided.dragHandleProps}		
					    			>
											<Task 
												innerRef={provided.innerRef}
												last={task.priority === this.props.tasks.length -1 ? true : false}
												changePriority={this.props.changePriority}
												themes={this.props.themes}
												epics={this.props.epics}
												allTimeboxes={this.props.allTimeboxes}
												convertToEpic={this.props.convertToEpic}
												deleteTask={this.props.deleteTask}
												{...task}
											/>
										</div>

									)}
								</Draggable>
							)}
							{this.props.tasks.length === 0  &&
								<div className='card card-outline'>

								</div>}

					 </div>
				}
			}

class TasksColumn extends React.Component {
	state = {tasks: []}

	render() {
		return <div className='col-4'>
							<div className='container h3'>
								{this.props.colId}
							</div>
							<div className='row mt-2'>
								<Droppable droppableId={this.props.colId}>
									{(provided) => (

										<div
								      ref={provided.innerRef}
								      {...provided.draggableProps}
								      {...provided.dragHandleProps}
								    	>

								    	{this.props.colId !== 'Backlog' && this.props.tasks.length > 0 &&
									        <StartWorking 
									        	projectId={this.props.projectId}
									        	timeboxId={this.props.timeboxId}
									        	timeboxName={this.props.colId}
									        />
									      }
											<TaskList
											innerRef={provided.innerRef}
											{...provided.droppableProps}
											tasks={this.props.tasks}
											changePriority={this.props.changePriority} 
											themes={this.props.themes} 
											epics={this.props.epics}
											allTimeboxes={this.props.allTimeboxes}
											convertToEpic={this.props.convertToEpic}
											deleteTask={this.props.deleteTask}
											/>
											{provided.placeholder}
										</div>

									)}
								</Droppable>
							</div>
						</div>
					}
				}

class TasksContainer extends React.Component {
	state = {showAddTimeboxModalStatus: false}

	showAddTimeboxModal = () => {
		this.setState({showAddTimeboxModalStatus: true})
	}

	hideTimeboxModal = () => {
		this.setState({showAddTimeboxModalStatus: false})
	}

	addTimebox = (timeboxData) => {
		this.props.addTimebox(timeboxData)
	}

	render() {
		const backlog = this.props.allTimeboxes.find(tb => tb.title === 'Backlog')
		const timeboxes = this.props.allTimeboxes.filter(tb => tb.title !== 'Backlog')
		console.log(this.props.tasks)
		return  <div className='row lists'>
					<TasksColumn 
						colId={backlog.title}
						tasks={this.props.tasks.filter(task => task.timebox === backlog.title)}
						changePriority={this.props.changePriority} 
						themes={this.props.themes} 
						all_timeboxes={this.props.all_timeboxes}
						deleteTask={this.props.deleteTask}
					/>
				{timeboxes.map(timebox => 
					<TasksColumn
						key={timebox.id} 
						colId={timebox.title}
						timeboxId={timebox.id}
						projectId={this.props.projectId}
						tasks={this.props.tasks.filter(task => task.timebox === timebox.title)}
						changePriority={this.props.changePriority} 
						themes={this.props.themes} 
						all_timeboxes={this.props.all_timeboxes}
						deleteTask={this.props.deleteTask}
					/>
				)}
				<TimeboxModal
				  projectId={this.props.projectId}
					display={this.state.showAddTimeboxModalStatus}
					hide={this.hideTimeboxModal}
					addTimebox={this.addTimebox}
				/>
				{this.props.allTimeboxes.length === 1 &&
			        <AddTimebox 
			        	showAddTimeboxModal={this.showAddTimeboxModal}
		        	/>
			      }
			  </div>

	}
}

class TasksArea extends React.Component {
	render() {
		let height = ''
		if (document.getElementById("themes-container")) {
			let themes_height = document.getElementById("themes-container").offsetHeight
			let nav_height = 80
			let total = nav_height + themes_height
			height = String(total) + 'px'
		} else { 
			height = '200px'
		}
		const style = `${height}`
		return <div id='non-fixed-content' style={{top: style}}>
						<CreateTaskForm onSubmit={this.props.onSubmit} projectId={this.props.projectId}/>
						<TasksContainer
						 	tasks={this.props.tasks} 
							themes={this.props.themes} 
							allTimeboxes={this.props.allTimeboxes}
							deleteTask={this.props.deleteTask}
							projectId={this.props.projectId}
							addTimebox={this.props.addTimebox}
						/>
					</div>
	}
}	

export default TasksArea;
