import React from 'react';
import 'bootstrap';
import './modeswitcher.css';
import { NavLink } from 'react-router-dom';

class ModeSwitcher extends React.Component {
	render() {
		if (this.props.mode === 'planning') {

			return  <ul className="nav row mode-switcher">
						<li className='col-2 text-center '>
							<NavLink className='option' to={{pathname: `/`}}>Home</NavLink>
						</li>
						<li className='col-5 text-center selected'>
							<NavLink className='option' to={{pathname: `/planning/${this.props.projectId}`}}>Planning</NavLink>
						</li>
						<li className='nav-item col-5 text-center'>
							<NavLink className='option' to={{pathname: `/working/${this.props.projectId}/${this.props.timeboxId}`}}>Working</NavLink>
						</li>
					</ul>
		}

		else {

			return  <ul className="nav row mode-switcher">
						<li className='col-2 text-center'>
								<NavLink className='option' to={{pathname: `/`}}>Home</NavLink>
						</li>
						<li className='col-5 text-center'>
								<NavLink className='option' to={{pathname: `/planning/${this.props.projectId}`}}>Planning</NavLink>
						</li>
						<li className='nav-item col-5 text-center selected'>
								<NavLink className='option' to={{pathname: `/working/${this.props.projectId}`}}>Working</NavLink>
						</li>
					</ul>

		}
	}
}

export default ModeSwitcher
