class Header extends React.Component {
	render() {
		return <div className='col-12 header text-center'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}


class Task extends React.Component {
	render() {
		return <div className='card m-2'>
							<div className='card-body'>
								<div className='card-title'>
									{this.props.title}
									<span className='status badge bg-secondary'>{this.props.status}</span>
								</div>

								{ this.props.status == 'To Do' ? (
									<button className='btn btn-success'>1</button>
									) : (
									<div>2</div>
									)


							}
							</div>
						</div>
	}
}

class TaskList extends React.Component {
	render() {
		console.log(this.props.tasks)
		return <div className='row mt-2'>
						<div className='col-6 mx-auto'>
							{this.props.tasks.map(task => <Task key={task.id} {...task}/>)}
						</div>
					 </div>
	}
}						

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
  	event.preventDefault();
    const data = {title: this.state.title}
    const response = await fetch('/add_task', {
	    method: 'POST', // 
	    mode: 'cors', // 
	    cache: 'no-cache', // 
	    credentials: 'same-origin', 
	    headers: {
	      'Content-Type': 'application/json'
	    },
	    redirect: 'follow', 
	    referrerPolicy: 'no-referrer',
	    body: JSON.stringify(data)
  });
    const task = await response.json();
    this.props.onSubmit(task) //this is a hack. you should be retunring the whole array;
    this.setState({ title: '' });
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto'>Add Task</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class App extends React.Component {
	state = {tasks: []}
	componentDidMount() {
    fetch('/get_tasks')
      .then((response) => response.json())
      .then((data) => this.setState({ tasks: data['tasks'] }));

		};

		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...this.state.tasks, taskData],
    }));
  };
	render() {
		return <div className='row'>
						<Header />
						<CreateTaskForm onSubmit={this.addNewTask}/>
						<TaskList tasks={this.state.tasks}/>
					 </div>
	}
}

export default App;
