import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const BoardButton = (props) => {
	const path = '/board/' + String(props.board.board_id)
	return 	<div className='card mt-2 mb-2'>

	<Link to={{pathname: path, state: {board_id: props.board.board_id}}}>{props.board.board_title}</Link>
					</div>
}

class AddBoard extends React.Component {
	state = {title: ''}

	handleSubmit = (event) => {
		event.preventDefault()
		axios.post('/add_board', {
			title: this.state.title
		})
		.then( (response) => {
			this.props.addBoard(response.data.boards)
			this.setState({title: ''})
		})
	}
	render() {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="Enter Board Name" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto mb-4'>Add Board</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class BoardsList extends React.Component {
	render() {
		return 	<div className='row'>
							<div className='col-6 text-center mx-auto mt-5 border border-secondary'>
							<h2> Boards </h2>
							<div className='container'>
								{this.props.boards.map( board =>
									<BoardButton board={board} />
									)}
							</div>
							</div>
						</div>						
	}
}

class OldApp extends React.Component {
	state = {boards: [], data_received: false}

	addBoard = (boards_data) => {
		this.setState(prevState => (
			{boards: boards_data}))
	}

	getBoards = async () => {
    const resp = await axios.get(`/get_boards`)
    const boardsList = resp.data['boards']
    this.setState({boards: boardsList});
    this.setState({data_received: true})
  };

  componentWillMount() {
    this.getBoards();
  }

	render() {
		if (this.state.data_received === false){
			return <div>loading</div>
		} else {
			return <div>
							<AddBoard addBoard={this.addBoard} />
							<BoardsList boards={this.state.boards}/>
						 </div>
		}
	}
}






// new app

const ProjectButton = (props) => {
	const path = '/planning/' + String(props.project.projectId)
	return 	<div className='card mt-2 mb-2'>

	<Link to={{pathname: path, state: {project: props.project.projectId}}}>{props.project.projectTitle}</Link>
					</div>
}


class ProjectsList extends React.Component {
	render() {
		return 	<div className='row'>
							<div className='col-6 text-center mx-auto mt-5 border border-secondary'>
							<h2> Projects </h2>
							<div className='container'>
								{this.props.projects.map( project =>
									<ProjectButton project={project} />
									)}
							</div>
							</div>
						</div>						
	}
}

class AddProject extends React.Component {
	state = {title: ''}

	handleSubmit = (event) => {
		event.preventDefault()
		axios.post('/add_project', {
			title: this.state.title
		})
		.then( (response) => {
			this.props.addProject(response.data.projects)
			this.setState({title: ''})
		})
	}
	render() {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="Enter Project Name" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto mb-4'>Add Project</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}



class App extends React.Component {
	state = {projects: [], data_received: false}

	addProject = (projectsData) => {
		this.setState(prevState => (
			{projects: projectsData}))
	}

	getProjects = async () => {
    const resp = await axios.get(`/get_projects`)
    const projectsList = resp.data['projects']
    this.setState({projects: projectsList});
    this.setState({data_received: true})
  };

  componentWillMount() {
    this.getProjects();
  }

	render() {
		if (this.state.data_received === false){
			return <div>loading</div>
		} else {
			return <div>
							<AddProject addProject={this.addProject} />
							<ProjectsList projects={this.state.projects}/>
						 </div>
		}
	}
}


export default App;
