  		const resp = await axios.get(`/get_tasks?project_id=${projectId}`)
  		console.log(resp)
  	}

	tasks = 	[{title: 'test 1'}, 
				{title: 'test 2'}
				]

	render() {
		return <div className='row'>
				<Link to={{pathname: '/planning'}}>Planning</Link>
				<h1>Working</h1>
				<Tasks tasks={this.tasks} />
			   </div>
	}
}

export default Working
