import React from 'react'
import { Form } from "react-bootstrap";

class AddItem extends React.Component {

	render () {
		return  <Form onSubmit = {this.addItem} >
					<div className="row mb-3">
						{this.formGroups}
					</div>
					<div className='row'>
						<div className='col-6 mx-auto text-center'>
							<input className='btn btn-primary mb-2' type="submit" value={this.cta}/>
						</div>
					</div>
				</Form>
	}
}

const AddItemForm = ({ onSubmit, children }) => (
  <form onSubmit={onSubmit}>{children}</form>
);

const Button = ({ onClick, type = 'button', children }) => (
  <button type={type} onClick={onClick}>
    {children}
  </button>
);

const InputField = ({ value, onChange, children }) => (
  <label>
    {children}
    <input
      type="text"
      value={value}
      onChange={event => onChange(event.target.value)}
    />
  </label>
);


export {AddItem, AddItemForm, Button, InputField}
