import React from 'react';
import axios from 'axios';
import deleteIcon from './delete_icon.png';

class AddTheme extends React.Component {
	state = {title: ''}

	addTheme = async (event) => {
		event.preventDefault()
		axios.post('/add_theme', {
		  title: this.state.title,
    	  project_id: this.props.project_id
		})
		.then((response) => {
		  this.props.onSubmit(response.data)
		}, (error) => {
		  console.log(error);
		});
		this.setState({title: ''})

	}

	form = () => {
		return <form className='add-element-form' onSubmit={this.addTheme}>
										 	<div className='form-group element-input '>
												<input
													className='form-control form-control m-2'
								          type="text" 
								          value={this.state.title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder='Add Theme'
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}

class Themes extends React.Component {
	filterByTheme = (theme) => {
		this.props.filterByStatus(theme);
		if (theme === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	deleteTheme = () => {
		console.log('abc')
	}



	render() {
		return  <div className='row'>
					<div className='col-12'>
						<div className='container mx-auto text-center'>
						<h4>Themes</h4>
						</div>
						<AddTheme />
					</div>
				</div>			
	}
}


export default Themes
