import React from 'react';
import axios from 'axios';
import Board from './board.js';

class BoardsList extends React.Component {
	render() {
		console.log(this.props.boards)
		return 	<div>
							<h1> Boards </h1>
							<h3>{this.props.boards[0]['board_title']}</h3>

						</div>
	}
}

class App extends React.Component {
	state = {boards: [], data_received: false}

	getBoards = async () => {
    const resp = await axios.get(`/get_boards`)
    const boardsList = resp.data['boards']
    this.setState({boards: [boardsList]});
    this.setState({data_received: true})
  };

  componentDidMount() {
    this.getBoards();
  }

	render() {
		if (this.state.data_received === false){
			return <div>loading</div>
		} else {
			console.log(this.state.boards)
			return <BoardsList boards={this.state.boards}/>
		}
	}
}


export default App;
