import React from 'react';
import axios from 'axios';
import deleteIcon from './../img/delete_icon.png';
import { Droppable } from 'react-beautiful-dnd';
import { Modal, Form} from "react-bootstrap";
import './themes.css'

class AddTheme extends React.Component {
	state = {title: ''}

	addTheme = async (event) => {
		event.preventDefault()
		axios.post('/add_theme', {
		  title: this.state.title,
    	project_id: this.props.projectId
		})
		.then((response) => {
		  this.props.addTheme(response.data)
		}, (error) => {
		  console.log(error);
		});
		this.setState({title: ''})
	}

	form = () => {
		return 	<div 
								className='add-theme container card col-4'
							>
							<div 
								className='theme-title'
							>
								<div>
									+ Add Theme
								</div>
							</div>
			  		</div>
		}

	render() {
						return this.form()
	}
}

const Theme = (props) => {
	const deleteTheme = (themeId) => {
			axios.post('/delete_theme', {
				theme_id: themeId,
				project_id: props.projectId
			}).then( (response) => {
				props.deleteTheme(response.data.themes)
			}, (error) => {
				console.log(error)
			})
		} 
		const theme = String('Theme:' + props.data.id)

	return  <Droppable droppableId={theme}>
						{(provided) => (
							<div 
								ref={provided.innerRef} 
								className='container card theme col-4' 
								style={{backgroundColor: props.data.color}}
							>
								<div 
									onClick = {() => props.filterByTheme(props.title)} 
									className='theme-title'
								>
									<div>
										{props.data.title}
									</div>
								</div>
								<div className='theme-delete-icon'>
									<img 
										alt="delete-icon" 
										className='delete-icon' 
										onClick={() => deleteTheme(props.data.id)} 
										src={deleteIcon}
									>
									</img>
								</div>
								{provided.placeholder}
			  			</div>
			  		)}
					</Droppable>
}




class Themes extends React.Component {

	constructor(props) {
    super(props);

    this.state = {
      themes: props.themes,
      showAddThemeModalStatus: false
    };
  }

  static getDerivedStateFromProps(nextProps, prevState){
			   if(nextProps.themes !== prevState.themes){
			     return { themes: nextProps.themes};
			  }
			  else return null;
			}

	filterByTheme = (theme) => {
		this.props.filterByTheme(theme);
		if (theme === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	addTheme = (themedata) => {
		this.setState({showAddThemeModalStatus: false})
  };

  showAddThemeModal = () => {
  	this.setState({showAddThemeModalStatus: true})
  }

  	deleteTheme = (remainingThemes) => {
			this.setState(prevState => ({
			themes: remainingThemes,
	    }));
  };



	render() {
		return  <div className='row'>
							<div className='col-12'>
								<div className='container mx-auto text-center'>
								<h4>Themes</h4>
								</div>
								<div className='row'>
									{this.state.themes.map(theme => 
										<Theme 
											projectId={this.props.projectId} 
											key={theme.id} data={theme} 
											filterByTheme={this.filterByTheme}
											title={theme.title}
											deleteTheme={this.deleteTheme}
										/>)}
									<AddTheme 
									projectId={this.props.projectId}
									onClick= {() => this.setState({showAddThemeModalStatus: true})}/>
								</div>
							</div>
							<Modal show={this.state.showAddThemeModalStatus}>
					      <Modal.Header onClick= {() => this.setState({showAddThemeModalStatus: false})}closeButton>Add Theme</Modal.Header>
					      <Modal.Body>
					      	<Form onSubmit = {this.addTheme}>
							  <div className="row mb-3">
							    <Form.Group className='col-12' controlId="formTimeboxTitle">
							      <Form.Label>Name</Form.Label>
							      <Form.Control type="text" onChange={event => this.setState({ newThemeTitle: event.target.value })}/>
							    </Form.Group>
							  </div>
							  <input className='btn btn-primary' type="submit" value="Submit" />
							</Form>
					      </Modal.Body>
					    </Modal>
						</div>



	}
}


export default Themes
