class Header extends React.Component {
	render() {
		return <div className='col-12 header text-center'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

const Button = (props) => {
	return 	<button className='btn btn-secondary' onClick={props.action}>{props.body}</button>

}


class Task extends React.Component {
	state = {id: this.props.id, title: this.props.title, status:this.props.status}
	markAsDone = () => {
		this.props.markAsDone(this.state.id)
    this.setState({status: 'Done'})
		axios.post('/edit_task_status', {
		  id: this.state.id,
		  status: 'Done'
		});
	}

	markAsInProgress = () => {
		console.log('in progress')
	}

	render() {
		return <div className='card m-2'>
							<div className='card-body'>
								<div className='card-title'>
									{this.state.title}
									<span className='status badge bg-secondary mx-3'>{this.state.status}</span>
								</div>

								{ this.state.status == 'To Do' ? (
									<Button action={this.markAsInProgress} body='Start Work' />
									) : this.state.status == 'In Progress' ? (
									<Button action={this.markAsDone} body='Mark as Done'/>
									) : this.state.status == 'Done' ? (
									<Button action={this.reopen} body='Reopen'/>
									)
									: (
									<div></div>
									)


							}
							</div>
						</div>
	}
}

class TaskList extends React.Component {
	state = {tasks : []};

	markAsDone = (id) => {
		const obj = this.state.tasks.find(o => o.id === id);
		obj['status'] = 'Done'
	}

	getTasks = async () => {
    const resp = await axios.get(`/get_tasks`);
    this.setState({ tasks: resp.data['tasks'] });
  };



  async componentDidMount() {
    	this.getTasks();
    }

	render() {
		return <div className='row mt-2'>
						<div className='col-6 mx-auto'>
							{this.state.tasks.map(task => <Task markAsDone={this.markAsDone} key={task.id} {...task}/>)}
						</div>
					 </div>
	}
}

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
    const data = {title: this.state.title}
    const response = await fetch('/add_task', {
	    method: 'POST', // 
	    mode: 'cors', // 
	    cache: 'no-cache', // 
	    credentials: 'same-origin', 
	    headers: {
	      'Content-Type': 'application/json'
	    },
	    redirect: 'follow', 
	    referrerPolicy: 'no-referrer',
	    body: JSON.stringify(data)
  });
    const task = await response.json();
    this.setState({ title: '' });
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto'>Add Task</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class App extends React.Component {
		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...this.state.tasks, taskData],
    }));
  };

	render() {
		return <div className='row'>
						<Header />
						<CreateTaskForm onSubmit={this.addNewTask}/>
						<TaskList />
					 </div>
	}
}


export default App;
