import axios from 'axios';

class GetRequest {
  constructor(endpoint, queryString) {
    this.endpoint = endpoint;
    this.queryString = queryString
  }
  call = async () => {
    const resp = await axios.get(this.endpoint + '?' + this.queryString)
    return resp;
  }
}

export default GetRequest
