import React from 'react';
import 'bootstrap';
import axios from 'axios';
import { useHistory } from "react-router-dom";
import ModeSwitcher from './../ModeSwitcher/ModeSwitcher.js'

const CTA = (props) => {
	if (props.taskStatus === 'To Do') {
		return <div className='btn btn-primary' onClick = {() => props.changeStatus('In Progress')}>Start</div>
	} else if ( props.taskStatus === 'In Progress') {
		return <div className='btn btn-success' onClick = {() => props.changeStatus('Done')}>Complete</div>
	} else {
		return <div className='btn btn-warning' onClick = {() => props.changeStatus('To Do')}>Actually, I'm Not Done</div>
	}
}

const CloseTimebox = (props) => {
	const history = useHistory();
	const close = () => {
		axios.post('/update_timebox_status', {
		project_id: props.projectId,
		timebox_id: props.timeboxId,
		status: 'Closed'
		})
	const path = {pathname: '/planning', query: {projectId: props.projectId}}
	history.push(path);
	}

	return <div 
			className='btn btn-primary w-100 mt-2'
			onClick = {() => close()}>
				Close Timebox
			</div>
}

const reopenTimeBox = (props) => {
	const history = useHistory();
	const reopen = () => {
		axios.post('/update_timebox_status', {
		project_id: props.projectId,
		timebox_id: props.timeboxId,
		status: 'To Do'
		})
	const path = {pathname: '/planning', query: {projectId: props.projectId}}
	history.push(path);
	}

	return <div 
			className='btn btn-secondary w-100 mt-2'
			onClick = {() => reopen()}>
				Back to Planning
			</div>
}

class Task extends React.Component {

	constructor(props) {
    super(props);
    this.state = {...this.props.task};
  }

	changeStatus = (targetStatus) => {
		axios.post('/update_task_status', {
			task_id: this.props.task.id,
			target_status: targetStatus
		})
		this.setState({status: targetStatus})
	}

	render() {
		return <div className='card w-75 mx-auto mt-2'>
				<div className='card-body'>
					<div className='card-title'> 
						{this.props.task.title}
					</div>
					<div className='cta-container'>
							<CTA taskStatus={this.state.status} changeStatus={this.changeStatus} />
					</div>
				</div>
			   </div>
	}
}

class Tasks extends React.Component {
	render() {
		return <div className='col-12'>
				{this.props.tasks.map(task => 
					<Task 
						task={task}
						key={task.id}
					/>
				)}
			  </div>
	}
}

class Working extends React.Component {

	constructor(props) {
    super(props);
    const sp = new URLSearchParams(this.props.location.query);
	const projectId = sp.get("projectId")
	const timeboxId = sp.get("timeboxId")
	if (projectId === null) {
			this.props.history.push('/')
		};
	this.getTimeboxTasks(projectId, timeboxId).then(
		this.state = {dataReceived: true, projectId: projectId, timeboxId: timeboxId, tasks: []})
  }

  	getTimeboxTasks = async (projectId, timeboxId) => {
  		const resp = await axios.get(`/get_tasks_of_timebox?project_id=${projectId}&timebox_id=${timeboxId}`)
  		this.setState({tasks: resp.data.tasks})
  	}

	render() {
		if (this.state.dataReceived === false) {
			return<div>abc</div>
		}
		else {
			return 	<div>
						<div className='row working-page'>
							<div className='col-8 mx-auto'>
								<CloseTimebox 
									projectId={this.state.projectId}
									timeboxId={this.state.timeboxId} 
								/>
							</div>
							<Tasks tasks={this.state.tasks} />
						</div>
			  		</div>
			}
	}
}

export default Working
