class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleAddTask = async (event) => {
			event.preventDefault();
			const requestOptions = {
					method: 'POST',
					headers: { 'Content-Type': 'application/json' },
					body: JSON.stringify({ title: this.state.title })
					};
			const response = await fetch('/add_task', requestOptions);
			const data = await response.json();
			console.log(data);
		};
	render () {
		return <form onSubmit={this.handleAddTask}>
						<input 
		          type="text" 
		          value={this.state.title}
		          onChange={event => this.setState({ title: event.target.value })}
		          placeholder="Task Name" 
		          required 
		        />
						<button>Add Task</button>
					 </form>
	}
}

class Header extends React.Component {
	render() {
		return <div className='header'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

class App extends React.Component {


	render() {
		const tasks = ['do something', 'do nothing']
		return <div>
						<Header />
						<CreateTaskForm />
						<TaskList tasks={tasks}/>
					 </div>
	}
}

export default App;
