import React, {useState} from 'react'
import axios from 'axios';


const AddTimebox = ({onSubmit, projectId, updateTimeboxes}) => {
	const [title, setTitle] = useState('');
	const [goalOne, goalTwo, goalThree] = useState('')
	const setGoal = (goal) => {
		goal = useState('')
	}

	const addTimebox = (event) => {
		event.preventDefault()
		const goals = [goalOne, goalTwo, goalThree]
		axios.post('/add_timebox', {
			project_id: projectId,
			title: title,
			goals: goals
		}).then((response) => {
			updateTimeboxes(response.data)
		}, (error) => {
		  console.log(error);
		});
	}

	return (
	    <Form
	      onSubmit={addTimebox}
	      cta='Add Task'
	    >
	      <FullWidthInputField value={title} onChange={setTitle}>
	      </FullWidthInputField>
	      <TripleInput valueOne={goalOne} valueTwo={goalTwo} valueThree={goalThree} onChange={setGoal}>
	      </TripleInput>
	    </Form>
	  );
	};


const AddTask = ({onSubmit, projectId, updateTasks}) => {
	const [title, setTitle] = useState('');

	const addTask = async (event) => {
		event.preventDefault()
	    axios.post('/add_task', {
	    	title: title,
	    	project_id: projectId
	    })
	    .then((response) => {
	    	const taskData = response.data.tasks[0]
	    	setTitle('')
			updateTasks(taskData)
		}, (error) => {
			console.log(error);
		});    
	  };

	return (
	    <Form
	      onSubmit={addTask}
	      cta='Add Task'
	    >
	      <FullWidthInputField value={title} onChange={setTitle}>
	      </FullWidthInputField>

	    </Form>
	  );
	};

const AddTheme = ({ onSubmit, projectId, updateThemes }) => {
	  const [title, setTitle] = useState('');

	  const addTheme = async (event) => {
	  	event.preventDefault()
		axios.post('/add_theme', {
		  title: title,
		  project_id: projectId
		  })
		.then((response) => {
		  updateThemes(response.data)
		}, (error) => {
		  console.log(error);
		});
		setTitle('')
		}

	  return (
	    <Form
	      onSubmit={addTheme}
	      cta='Add Theme'
	    >
	      <FullWidthInputField value={title} onChange={setTitle}>
	      </FullWidthInputField>

	    </Form>
	  );
	};

const Form = ({ onSubmit, cta, children }) => (
  <form onSubmit={onSubmit}>
    <div className="row mb-3">
		{children}
	</div>
	<div className='row'>
		<div className='col-6 mx-auto text-center'>
			<input className='btn btn-primary mb-2' type="submit" value={cta}/>
		</div>
	</div>
  </form>
);


const FullWidthInputField = ({ value, onChange, children }) => (
    <div className='col-10 mx-auto'>
		<input className='form-control' placeholder="Theme Name" value={value} type="text" onChange={event => onChange(event.target.value)}/>
	</div>
);

const TripleInput = ({ valueOne, valueTwo, valueThree, onChange, children }) => (
    <div className='col-12 mx-auto mt-2 goals-group'>
		<input className='form-control' placeholder="Goal 1" value={valueOne} type="text" onChange={event => onChange(event.target.value)}/>
		<input className='form-control' placeholder="Goal 2" value={valueTwo} type="text" onChange={event => onChange(event.target.value)}/>
		<input className='form-control' placeholder="Goal 3" value={valueThree} type="text" onChange={event => onChange(event.target.value)}/>
	</div>
);


export {AddTheme, AddTask, AddTimebox}
