import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import { Link } from 'react-router-dom';

class ModeSwitcher extends React.Component {
	render() {
		if (this.props.mode === 'planning') {

			return  <ul className="nav row">
					<li className='nav-item col-6 text-center'>
						<div className='mode-switcher-enabled'>
							<Link to={{pathname: `/working/${this.props.projectId}`}}>Working a</Link>
						</div>
					</li>
					<li className='col-6 text-center '>
						<div className='mode-switcher-disabled'>
							<Link to={{pathname: `/planning/${this.props.projectId}`}}>Planning</Link>
						</div>
					</li>
				</ul>
		}

		else {

			return  <ul className="nav row">
					<li className='nav-item col-6 text-center'>
						<div className='mode-switcher-disabled'>
							<Link to={{pathname: `/working/${this.props.projectId}`}}>Working</Link>
						</div>
					</li>
					<li className='col-6 text-center'>
						<div className='mode-switcher-enabled'>
							<Link to={{pathname: `/planning/${this.props.projectId}`}}>Planning</Link>
						</div>
					</li>
				</ul>

		}
	}
}

export default ModeSwitcher
