import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import Task from './task.js'

class AddTheme extends React.Component {
	state = {title: ''}

	addTheme = async (event) => {
		await axios.post('/add_theme', {
		  title: this.state.title
		});
	}

	render() {
		return <form className='add-theme-form' onSubmit={this.addTheme}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-sm m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="Add New theme" 
				          required 
				        />
				        </div>
						 </form>
	}
}

class Theme extends React.Component {
	render() {
		return <div className='theme-button' onClick = {() => this.props.filterByStatus(this.props.themeTitle)} style={{backgroundColor: this.props.color}}>{this.props.themeTitle}</div>
	}
}

class ThemeRow extends React.Component {
	render() {
		return <div className='row'>
								{this.props.themes !== [] ?  (
								 <div className='col-12'>
									{this.props.themes.map(theme => 
										<Theme key={theme.id} themeTitle={theme.title} color={theme.color} filterByStatus={this.props.filterByStatus}/>)}
										<AddTheme />
								 </div> )
								: <div></div> }						 	
					 </div>

	}

}

class TasksColumn extends React.Component {

	changePriority = async (task_id, priority) => {
		const resp = await axios.post('/update_priority', {
			task_id: task_id,
			priority: priority
			});
			await this.setState({tasks: resp.data['tasks']})
		}	

	render() {
		const tasks = [...this.props.tasks].sort((a,b) => a.priority > b.priority ? 1 : -1)
		return <div className='col-4'>
							<div className='row mt-2'>
								<div className='col-6 mx-auto w-100'>
									{tasks.map(task => 
										<Task key={task.id}
													last={task.priority === tasks.length -1 ? true : false}
										 			updateStatus={this.props.updateStatus}
													changePriority={this.changePriority}
													themes={this.props.themes}
													{...task}
													/>)
									}
								</div>
							</div>
						</div>
	}
}

class TaskList extends React.Component {
	state = {taskSplittingComplete: false};

  splitTasksByStatus = () => {
  	const doneOnly = this.props.tasks.filter(task => task.status === 'Done');
  	const inProgressOnly = this.props.tasks.filter(task => task.status === 'In Progress'); 
  	const toDoOnly = this.props.tasks.filter(task => task.status === 'To Do');

  	this.setState({ doneTasks: doneOnly });
  	this.setState({ inProgressTasks: inProgressOnly });
  	this.setState({ toDoTasks: toDoOnly})
  	this.setState({ taskSplittingComplete: true})
  }	

  async componentDidMount() {
    	await this.splitTasksByStatus()

    }

  updateStatus = (id, status) => {
		const obj = this.props.tasks.find(o => o.id === id);
		obj['status'] = status
		//should probably set state of all_tasks here too. Actually just setting all tasks might be the way to go here
		if (status === 'Done') {
			const newInProgressTasks = this.state.inProgressTasks.filter(task => task !== obj)
			this.setState({inProgressTasks: newInProgressTasks})
			this.setState({doneTasks: [...this.state.doneTasks, obj]})
		} else if (status === 'In Progress'){
			const newToDoTasks = this.state.toDoTasks.filter(task => task !== obj)
			this.setState({toDoTasks: newToDoTasks})
			this.setState({inProgressTasks: [...this.state.inProgressTasks, obj]})
		} 
		else if (status === 'To Do'){
			const newDoneTasks = this.state.doneTasks.filter(task => task !== obj)
			this.setState({doneTasks: newDoneTasks})
			this.setState({toDoTasks: [...this.state.toDoTasks, obj]})
		}
	}




	render() {
		return <div className='row'>
						{ this.state.taskSplittingComplete === true ? (
						<React.Fragment>
						<TasksColumn tasks={this.state.toDoTasks}  updateStatus={this.updateStatus} themes={this.state.themes}/>
						<TasksColumn tasks={this.state.inProgressTasks}  updateStatus={this.updateStatus} themes={this.state.themes}/>
						<TasksColumn tasks={this.state.doneTasks}  updateStatus={this.updateStatus} themes={this.state.themes}/>
						</React.Fragment>
						) : (
									<div></div>
								)}
					</div>

	}
}

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
    const data = {title: this.state.title}
    await fetch('/add_task', {
	    method: 'POST', // 
	    mode: 'cors', // 
	    cache: 'no-cache', // 
	    credentials: 'same-origin', 
	    headers: {
	      'Content-Type': 'application/json'
	    },
	    redirect: 'follow', 
	    referrerPolicy: 'no-referrer',
	    body: JSON.stringify(data)
  });
    this.setState({ title: '' });
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto mb-4'>Add Task</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class Header extends React.Component {
	render() {
		return <div className='col-12 header text-center'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

class App extends React.Component {
		state = {tasks: [], themes: [], data_received:false}
		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...this.state.tasks, taskData],
    }));
  };

  	filterByStatus = (status) => {
	  		const filteredTasks = this.state.tasks.map(task => task.theme === status);
	  		this.setState({tasks: filteredTasks})
  	}

  	getTasks = async () => {
    const resp = await axios.get(`/get_tasks`);
    await this.setState({ tasks: resp.data['tasks'], themes:resp.data['themes'] });
    this.setState({data_received: true})
  };

  	updateStatus = (id, status) => {
  		let all_tasks = this.state.tasks
			const obj = all_tasks.find(o => o.id === id);
			all_tasks.filter(task => task !== obj);
			this.setState({tasks: [...all_tasks]})
			obj['status'] = status
			this.setState({tasks: [...all_tasks, obj]})
			console.log('status updated')
			this.forceUpdate()
		}

   	componentDidMount() {
    	this.getTasks();
    }

    r = Math.random()

	render() {

		return this.state.data_received === true ? (
						<div className='row'>
						<div>{this.r}</div>
						<Header />
						<CreateTaskForm onSubmit={this.addNewTask}/>
						<ThemeRow filterByStatus={this.filterByStatus} themes={this.state.themes}/>
						<TaskList tasks={this.state.tasks} themes={this.state.themes} updateStatus={this.updateStatus}/>
					 </div> )
					: <div></div>

	}
}


export default App;
