import React from 'react';
import axios from 'axios';


class AddElement extends React.Component {
	form = (action, title, placeholder, className) => {
		return <form className={className} onSubmit={action}>
										 	<div className='form-group'>
												<input
													className='form-control form-control-sm m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder={placeholder}
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}

class SideMenu extends React.Component {
	render() {
		return 	<div>
					<ThemeRow themes={this.props.themes} filterByStatus={this.props.filterByStatus}/>
				</div>
	}
}

const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByStatus('')}>clear</div>
}

class Theme extends React.Component {
	render() {
		return <div className='theme-button' onClick = {() => this.props.filterByStatus(this.props.themeTitle)} style={{backgroundColor: this.props.color}}>{this.props.themeTitle}</div>
	}
}


class AddTheme extends AddElement {
	state = {title: ''}
	addTheme = async (event) => {
		await axios.post('/add_theme', {
		  title: this.state.title
		});
	}
	render() {
		return this.form(this.addTheme, this.state.title, 'Add New Theme', 'add-theme-form')
	}
}

class ThemeRow extends React.Component {
	state = {filtering: false}
	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}
	render() {
		return <div className='row'>
								{this.props.themes !== [] ?  (
								 <div className='col-12'>
									{this.props.themes.map(theme => 
										<Theme key={theme.id} themeTitle={theme.title} color={theme.color} filterByStatus={this.filterByStatus}/>)}
										{this.state.filtering === true ? (
											<ClearFilters filterByStatus={this.filterByStatus}/>) : <div></div> }
										<AddTheme />
								 </div> )
								: <div></div> }						 	
					 </div>
	}				
}


export default SideMenu;
