import React from 'react';
import axios from 'axios';
import Board from './board.js';
import { Link } from 'react-router-dom';

const BoardButton = (props) => {
	const path = '/board/' + String(props.board.board_id)
	return 	<div className='card'>

	<Link to={{pathname: path, state: {board_id: props.board.board_id}}}>{props.board.board_title}</Link>
					</div>
}

class BoardsList extends React.Component {
	render() {
		return 	<div className='row'>
							<div className='col-6 text-center mx-auto mt-5 border border-secondary'>
							<h2> Boards </h2>
							<div className='container'>
								{this.props.boards.map( board =>
									<BoardButton board={board} />
									)}
							</div>
							</div>
						</div>						
	}
}

class App extends React.Component {
	state = {boards: [], data_received: false}

	getBoards = async () => {
    const resp = await axios.get(`/get_boards`)
    const boardsList = resp.data['boards']
    this.setState({boards: boardsList});
    this.setState({data_received: true})
  };

  componentDidMount() {
    this.getBoards();
  }

	render() {
		if (this.state.data_received === false){
			return <div>loading</div>
		} else {
			return <BoardsList boards={this.state.boards}/>
		}
	}
}


export default App;
