import React from 'react';
import { useHistory, Link, Redirect } from "react-router-dom";
import 'bootstrap';
import axios from 'axios';
import './planning.css';
import Task from './../Task/Task.js';
import Themes from './../Themes/Themes.js'
import { TimeboxModal, AddTimebox, StartWorking } from './../Timebox/Timebox.js';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';


class TaskList extends React.Component {
	render() {		
		return <div className='col-6 mx-auto w-100'>
							{this.props.tasks.map((task, index) =>

								<Draggable key={task.id} draggableId={String(task.id)} index={index}>
									{provided => (

										<div  
											ref={provided.innerRef}
											{...provided.draggableProps}
			      					{...provided.dragHandleProps}		
					    			>
											<Task 
												innerRef={provided.innerRef}
												last={task.priority === this.props.tasks.length -1 ? true : false}
									 			updateStatus={this.props.updateStatus}
												changePriority={this.props.changePriority}
												themes={this.props.themes}
												epics={this.props.epics}
												allTimeboxes={this.props.allTimeboxes}
												convertToEpic={this.props.convertToEpic}
												deleteTask={this.props.deleteTask}
												{...task}
											/>
										</div>

									)}
								</Draggable>
							)}
					 </div>
				}
			}

class TasksColumn extends React.Component {
	state = {tasks: []}

	render() {
		return <div className='col-4 mt-2'>
							<div className='container'>
								{this.props.colId}
							</div>
							<div className='row mt-2'>
								<Droppable droppableId={this.props.colId}>
									{(provided) => (

										<div
								      ref={provided.innerRef}
								      {...provided.draggableProps}
								      {...provided.dragHandleProps}
								    	>

								    	{this.props.colId !== 'Backlog' && this.props.tasks.length > 0 &&
									        <StartWorking 
									        	projectId={this.props.projectId}
									        	timeboxId={this.props.timeboxId}
									        />
									      }
											<TaskList
											innerRef={provided.innerRef}
											{...provided.droppableProps}
											tasks={this.props.tasks}
											changePriority={this.props.changePriority} 
											updateStatus={this.props.updateStatus} 
											themes={this.props.themes} 
											epics={this.props.epics}
											allTimeboxes={this.props.allTimeboxes}
											convertToEpic={this.props.convertToEpic}
											deleteTask={this.props.deleteTask}
											/>
											{provided.placeholder}
										</div>

									)}
								</Droppable>
							</div>
						</div>
					}
				}

class TasksContainer extends React.Component {
	state = {showAddTimeboxModalStatus: false}

	showAddTimeboxModal = () => {
		this.setState({showAddTimeboxModalStatus: true})
	}

	hideTimeboxModal =() => {
		this.setState({showAddTimeboxModalStatus: false})
	}

	render() {
		const backlog = this.props.allTimeboxes.find(tb => tb.title === 'Backlog')
		const timeboxes = this.props.allTimeboxes.filter(tb => tb.title !== 'Backlog')
		return  <div className='row'>
					<TasksColumn 
						colId={backlog.title}
						tasks={this.props.tasks.filter(task => task.timeboxes[0] === backlog.title)}
						changePriority={this.props.changePriority} 
						updateStatus={this.props.updateStatus} 
						themes={this.props.themes} 
						all_timeboxes={this.props.all_timeboxes}
						deleteTask={this.props.deleteTask}
					/>
				{timeboxes.map(timebox => 
					<TasksColumn
						key={timebox.id} 
						colId={timebox.title}
						timeboxId={timebox.id}
						projectId={this.props.projectId}
						tasks={this.props.tasks.filter(task => task.timeboxes[0] === timebox.title)}
						changePriority={this.props.changePriority} 
						updateStatus={this.props.updateStatus} 
						themes={this.props.themes} 
						all_timeboxes={this.props.all_timeboxes}
						deleteTask={this.props.deleteTask}
					/>
				)}
				<TimeboxModal
				  projectId={this.props.projectId}
					display={this.state.showAddTimeboxModalStatus}
					hide={this.hideTimeboxModal}
				/>
				{this.props.allTimeboxes.length === 1 &&
			        <AddTimebox 
			        	showAddTimeboxModal={this.showAddTimeboxModal}
		        	/>
			      }
			  </div>

	}
}						

class CreateTaskForm extends React.Component {
	state = { title: ''};

	handleSubmit = async (event) => {
    axios.post('/add_task', {
    	title: this.state.title,
    	project_id: this.props.projectId
    })
    .then((response) => {
		  this.props.onSubmit(response.data)
		}, (error) => {
		  console.log(error);
		});
		  event.preventDefault()
    	this.setState({title : ''})   
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				       </div>
			         <div className='col text-center'>
								<button className='btn btn-primary mx-auto mb-4'>Add Task</button>
							 </div>
						 </form>
						</div>
					 </div>
	}
}

