import React from 'react';
import axios from 'axios';
import './header.css';
import { Link } from 'react-router-dom';


class Header extends React.Component {
	constructor(props) {
    super(props);
    this.state = {isLoggedIn: false, username: false};
    }

    logout = () => {
    	axios.post('/auth/logout')
    	window.location.href = '/'
    }

    getUserData = async () => {
      const resp = await axios.get('/auth/me')
      this.setState({isLoggedIn: true});
      this.setState({dataReceived: true});
      this.setState({username: resp.data.username});
    };

    componentDidMount() {
      this.getUserData();
    }

	render() {
		const isLoggedIn = this.state.isLoggedIn;
	    let buttons;
	    if (isLoggedIn) {
	      buttons = <div>
	      				<button onClick={this.logout}>Logout</button>
	      				<div>{this.state.username}</div>
	      			</div>;

	    } else {
	      buttons = <div>
	      				<Link to="/login">Login</Link>
				 		<Link to="/register">Sign Up</Link>
	      			</div>
	    }
		return <div className='col-12 header text-center'>
				 <div className='title'>Todoodleoo</div>
				 <div className='subtitle'>Smart to-do list app</div>
				 {buttons}
				</div>
	}
}

export default Header
