import React from 'react';
import axios from 'axios';
import './header.css';
import { Link } from 'react-router-dom';


class Header extends React.Component {
	constructor(props) {
    super(props);
    this.state = {isLoggedIn: false, username: false};
    }

    logout = async () => {
    	await axios.post('/auth/logout');
    	window.location.href = '/'
    }

    getUserData = async () => {
      const resp = await axios.get('/auth/me')
      .then((response) => {
	      this.setState({isLoggedIn: true});
	      this.setState({dataReceived: true});
	      this.setState({username: response.data.username});
	    }, (error) => {
	      console.log('Not Logged In');
	    });

    };

    componentDidMount() {
      this.getUserData();
    }

	render() {
		const isLoggedIn = this.state.isLoggedIn;
	    let buttons;
	    if (isLoggedIn) {
	      buttons = <div>
	      						<div>{this.state.username}</div>
	      						<div className='btn btn-dark' onClick={this.logout}>Logout</div>
	      					</div>;

	    } else {
	      buttons = <div>
			      				<Link to="/login">Login</Link>
						 				<Link to="/register">Sign Up</Link>
			      			</div>
	    }
		return <div className='row header text-center'>
							<div class='col-3'>
								<Link to='/'>Home</Link>
							</div>
							<div class='col-6'>
								<div className='title'>Todoodleoo</div>
						 		<div className='subtitle'>Smart to-do list app</div>
							</div>
							<div class='col-3'>
								{buttons}
							</div>
						</div>
			}
}

export default Header
