import React from 'react';
import axios from 'axios';
import './header.css';
import { Link } from 'react-router-dom';


class Header extends React.Component {
	constructor(props) {
    super(props);
    this.state = {isLoggedIn: false, username: false};
    }

    logout = async () => {
    	await axios.post('/auth/logout');
    	window.location.href = '/'
    }

    getUserData = async () => {
      const resp = await axios.get('/auth/me')
      .then((response) => {
	      this.setState({isLoggedIn: true});
	      this.setState({dataReceived: true});
	      this.setState({username: response.data.username});
	    }, (error) => {
	      console.log('Not Logged In');
	    });

    };

    componentDidMount() {
      this.getUserData();
    }

	render() {
		const isLoggedIn = this.state.isLoggedIn;
	    let buttons;
	    if (isLoggedIn) {
	      buttons = <div>
	      						<div>{this.state.username}</div>
	      						<div className='btn btn-dark' onClick={this.logout}>Logout</div>
	      					</div>;

	    } else {
	      buttons = <div>
			      				<Link to="/login">Login</Link>
						 				<Link to="/register">Sign Up</Link>
			      			</div>
	    }
		return <header class="header">
					    <nav class="navbar navbar-expand-lg fixed-top py-3">
					        <div class="container"><a href="#" class="navbar-brand text-uppercase font-weight-bold">Transparent Nav</a>
					            <button type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler navbar-toggler-right"><i class="fa fa-bars"></i></button>

					            <div id="navbarSupportedContent" class="collapse navbar-collapse">
					                <ul class="navbar-nav ml-auto">
					                    <li class="nav-item active"><a href="#" class="nav-link text-uppercase font-weight-bold">Home <span class="sr-only">(current)</span></a></li>
					                    <li class="nav-item"><a href="#" class="nav-link text-uppercase font-weight-bold">About</a></li>
					                    <li class="nav-item"><a href="#" class="nav-link text-uppercase font-weight-bold">Gallery</a></li>
					                    <li class="nav-item"><a href="#" class="nav-link text-uppercase font-weight-bold">Portfolio</a></li>
					                    <li class="nav-item"><a href="#" class="nav-link text-uppercase font-weight-bold">Contact</a></li>
					                </ul>
					            </div>
					        </div>
					    </nav>
					</header>
			}
}

export default Header
