import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import Task from './task.js'

class AddElement extends React.Component {
	form = (action, title, placeholder, className) => {
		return <form className={className} onSubmit={action}>
										 	<div className='form-group'>
												<input
													className='form-control form-control-sm m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder={placeholder}
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}

class AddTheme extends AddElement {
	state = {title: ''}

	addTheme = async (event) => {
		await axios.post('/add_theme', {
		  title: this.state.title
		});
	}

	render() {
		return this.form(this.addTheme, this.state.title, 'Add New Theme', 'add-theme-form')
	}
}

class AddEpic extends AddElement {
	state = {title: ''}

	addEpic = () => {
		axios.post('/add_epic', {
			title: this.state.title
		})

	}

	render() {
		return this.form(this.addEpic, this.state.title, 'Add New epic', 'add-epic-form')
	}
}

class Theme extends React.Component {
	render() {
		return <div className='theme-button' onClick = {() => this.props.filterByStatus(this.props.themeTitle)} style={{backgroundColor: this.props.color}}>{this.props.themeTitle}</div>
	}
}

class Epic extends React.Component {
	render() {
		return <div className='epic-button' onClick = {() => this.props.filterByEpic(this.props.epicTitle)} style={{Color: this.props.color, border: `4px solid ${this.props.color}`}}><div className='epic-blob' style={{backgroundColor: this.props.color}}> </div>{this.props.epicTitle}</div>
	}
}

class EpicColumn extends React.Component {
	state = {filtering: false}

	filterByEpic = (epic) => {
		this.props.filterByEpic(epic)
		if (epic === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	render() {
		return <div className='col-3'>
						{this.props.epics.map(epic => 
							<Epic key={epic.id} epicTitle={epic.title} color={epic.color} filterByEpic={this.filterByEpic}/>)}
							<AddEpic />
					 </div> 					 	


	}

}

const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByStatus('')}>clear</div>
}

class ThemeRow extends React.Component {
	state = {filtering: false}

	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	render() {
		return <div className='row'>
								{this.props.themes !== [] ?  (
								 <div className='col-12'>
									{this.props.themes.map(theme => 
										<Theme key={theme.id} themeTitle={theme.title} color={theme.color} filterByStatus={this.filterByStatus}/>)}
										{this.state.filtering === true ? (
											<ClearFilters filterByStatus={this.filterByStatus}/>) : <div></div> }
										<AddTheme />
								 </div> )
								: <div></div> }						 	
					 </div>

	}

}

class TasksColumn extends React.Component {
	state = {tasks: []}


	render() {
		const tasks = [...this.props.tasks].sort((a,b) => a.priority > b.priority ? 1 : -1)
		return <div className='col-3'>
							<div className='row mt-2'>
								<div className='col-6 mx-auto w-100'>
									{tasks.map(task => 
										<Task key={task.id}
													last={task.priority === tasks.length -1 ? true : false}
										 			updateStatus={this.props.updateStatus}
													changePriority={this.props.changePriority}
													themes={this.props.themes}
													epics={this.props.epics}
													convertToEpic={this.props.convertToEpic}
													{...task}
													/>)
									}
								</div>
							</div>
						</div>
	}
}

const TaskList = (props) => {
  	const doneOnly = props.tasks.filter(task => task.status === 'Done');
 		const inProgressOnly = props.tasks.filter(task => task.status === 'In Progress'); 
  	const toDoOnly = props.tasks.filter(task => task.status === 'To Do');


		return  <React.Fragment>
						<TasksColumn tasks={toDoOnly}  
												changePriority={props.changePriority} 
												updateStatus={props.updateStatus} 
												themes={props.themes} 
												epics={props.epics}
												convertToEpic={props.convertToEpic}
												/>
						<TasksColumn tasks={inProgressOnly}  
												changePriority={props.changePriority} 
												updateStatus={props.updateStatus} 
												themes={props.themes} 
												epics={props.epics}
												convertToEpic={props.convertToEpic}
												/>
						<TasksColumn tasks={doneOnly}  
												changePriority={props.changePriority} 
												updateStatus={props.updateStatus} 
												themes={props.themes} 
												epics={props.epics}
												convertToEpic={props.convertToEpic}
												/>
						</React.Fragment>



}

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
    const data = {title: this.state.title}
    await fetch('/add_task', {
	    method: 'POST', // 
	    mode: 'cors', // 
	    cache: 'no-cache', // 
	    credentials: 'same-origin', 
	    headers: {
	      'Content-Type': 'application/json'
	    },
	    redirect: 'follow', 
	    referrerPolicy: 'no-referrer',
	    body: JSON.stringify(data)
  });
    this.setState({ title: '' });
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
