import React from 'react';
import { Modal, Form} from "react-bootstrap";
import { useHistory } from "react-router-dom";
import axios from 'axios';




const StartWorking = (props) => {
	const history = useHistory();
	const startTimebox = () => {
		const path = {pathname: '/working', query: {projectId: props.projectId, timeboxId: props.timeboxId}}
		axios.post('/update_timebox_status', {
		project_id: props.projectId,
		timebox_id: props.timeboxId,
		status: 'In Progress'
		})
		history.push(path)
	}
	return <div onClick={()=> startTimebox()} className='btn btn-primary'> Start Working</div>

}


const AddTimebox = (props) => {
	return <div className='col-4 mt-5'>
			 <div className='card'>
			 	<div className='card-body mx-auto'>
			 		<div onClick={()=> props.showAddTimeboxModal()} className='btn btn-primary'> Add a Timebox</div>
			 		<p>Timeboxes let you group tasks together into blocks for example
			 			To do today or to do before holiday</p>
			 	</div>
			 </div>
			</div>
}

class TimeboxModal extends React.Component {
	render() {
		return <Modal show={this.state.showAddTimeboxModalStatus}>
				  <Modal.Header onClick= {() => this.setState({showAddTimeboxModalStatus: false})}closeButton>Add Timebox</Modal.Header>
				  <Modal.Body>
				  	<Form onSubmit = {this.addTimebox}>
					  <div className="row mb-3">
					    <Form.Group className='col-12' controlId="formTimeboxTitle">
					      <Form.Label>Name</Form.Label>
					      <Form.Control type="text" onChange={event => this.setState({ newTimeboxTitle: event.target.value })}/>
					    </Form.Group>
					  </div>

					  <Form.Group className="mb-3" controlId="formTimeboxStartDate">
					    <Form.Label>Start Date</Form.Label>
					    <Form.Control type="date" />
					  </Form.Group>

					  <Form.Group className="mb-3" controlId="formTimeboxEndDate">
					    <Form.Label>End Date</Form.Label>
					    <Form.Control type="date" />
					  </Form.Group>

					  <input className='btn btn-primary' type="submit" value="Submit" />
					</Form>
				  </Modal.Body>
				</Modal>

	}
}

export { TimeboxModal, AddTimebox, StartWorking }
