import React from 'react';
import axios from 'axios';


class AddElement extends React.Component {
	form = (action, title, placeholder) => {
		return <form className='add-element-form' onSubmit={action}>
										 	<div className='form-group element-input '>
												<input
													className='form-control form-control m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder={placeholder}
								          required 
								        />
								        </div>
										 </form>
		}
	render() {
						return this.form()
	}
}



const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByStatus('')}>clear</div>
}

class AddTheme extends AddElement {
	state = {title: ''}
	addTheme = async (event) => {
		await axios.post('/add_theme', {
		  title: this.state.title
		});
	}
	render() {
		return this.form(this.addTheme, this.state.title, 'Add New Theme', 'add-theme-form')
	}
}
class AddEpic extends AddElement {
	state = {title: ''}

	addEpic = () => {
		axios.post('/add_epic', {
			title: this.state.title
		})

	}

	render() {
		return this.form(this.addEpic, this.state.title, 'Add New epic', 'add-epic-form')
	}
}

const MenuItem = (props) => {
	return <div onClick = {() => props.filterByItem(props.data.title)} className='menu-item'>
			<div className='menu-item-blob' style={{backgroundColor: props.data.color}}> </div>
			{props.data.title}
			</div>
}


class MenuSection extends React.Component {
	state = {filtering: false, expanded: false, display: false}
	html = (type) => {
		return <div className='card'>
					<div onClick = {() => {this.setState({display: true})}} className='card-header menu-section-header'>{this.props.title}</div>
					<div className='card-body menu-card-body' style={{display: this.state.display === true ? 'block' : 'none' }}>
					{type}
						{this.props.items.map(item => <MenuItem data={item} filterByItem={this.props.filterByItem}/>)}

					</div>

				</div>}
}

class Themes extends MenuSection {
	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	render() {
		return this.html(<AddTheme />)
	}			
}


class Timeboxes extends MenuSection {
	render() {
		return this.html(<AddTimebox />)
	}
}

class Epics extends MenuSection {
	state = {filtering: false}
	filterByEpic = (epic) => {
		this.props.filterByEpic(epic)
		if (epic === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}
	render() {
		return this.html(<AddEpic />)
	}				
}

class SideMenu extends React.Component {


	render() {
		return 	<div className='col-3'>
					<div className='accordion'>
						<Themes title='Themes' items={this.props.themes} filterByItem={this.props.filterByStatus} addItem={this.addTheme}/>
						<Epics title='Epics' items={this.props.epics} filterByItem={this.props.filterByEpic} addItem={this.addEpic}/>
						<Timeboxes title='Timeboxes' items={this.props.timeboxes} filterByItem={this.props.filterByTimebox} addItem={this.addTimebox}/>
					</div>
				</div>
	}
}



export default SideMenu;
