import React from 'react';
import axios from 'axios';
import pencil from './pencil.svg'
import up_arrow from './up_arrow.png'

const Button = (props) => {
	return 	<button className={props.styling} onClick={props.action}>{props.body}</button>
}

class RaisePriority extends React.Component {
	render () {
		return 	<img alt="raise-priority" onClick = {this.props.changePriority} className='priority-button raise-priority' src={up_arrow}></img>
	}
}

class DropPriority extends React.Component {
	render () {
		return  <img alt="drop-priority" onClick={this.props.changePriority} className='priority-button drop-priority' src={up_arrow}></img>
	}
}

class Task extends React.Component {
	state = {id: this.props.id,
	 		title: this.props.title, 
	 		status:this.props.status, 
	 		priority: this.props.priority,
	 		theme: this.props.theme,
	 		editing:false}

	updateStatus = async (new_status) => {
		const prev_status = this.state.status
		await axios.post('/update_task_status', {
		  id: this.state.id,
		  from_status: prev_status,
		  to_status: new_status
		});
		await this.props.updateStatus(this.state.id, new_status)

	}

	edit = () => {
		this.setState({editing: true})
	}

	save = (event) => {
		this.setState({editing: false})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: this.state.status,
		  theme: this.state.theme,
		  priority: this.state.priority
		});
	}

	editTaskName = (event) => {
		event.preventDefault()
		this.setState({editing: false})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: this.state.status,
		  theme: this.state.theme,
		  priority: this.state.priority
		});
	}

	changePriority = (direction) => {
		this.props.changePriority(this.state.id, direction)
	}

	render() {
		return <div className='card m-2'>
							{this.state.editing === false ? (
								<div className='card-body'>
										<img alt="edit-pencil" className='edit-pencil' onClick={this.edit} src={pencil}></img>
									<div className='card-title'>
										{this.state.title}
										<span className='status badge bg-secondary mx-3'>{this.state.status}</span>
										<span className='badge theme-badge mx-3' style={{backgroundColor: this.props.theme_color}}>{this.state.theme}</span>
									</div>
									{ this.state.status === 'To Do' ? (
										<React.Fragment>
											<Button action={() => this.updateStatus('In Progress')} body='Start Work' styling='btn btn-success' />
											{this.props.last === false ? (
											<DropPriority changePriority={() => this.changePriority('Down')}/>
											)	: (
											<div></div>
											)}
											{this.props.priority !== 0 ? (
												<RaisePriority changePriority={() => this.changePriority('Up')}/>) 
												: (
										<div></div>
									)}
										</React.Fragment>
										) : this.state.status === 'In Progress' ? (
										<Button action={() => this.updateStatus('Done')} body='Mark as Done' styling='btn btn-primary'/>
										) : this.state.status === 'Done' ? (
										<Button action={() => this.updateStatus('To Do')} body='Reopen' styling='btn btn-secondary'/>
										)
										: (
										<div></div>
										)


								}
								</div> ) : (
								<div className='card-body'>
									<Button action={this.save} body='save' styling='btn btn-primary'/>
									<div className='card-title'>
										<form onSubmit={this.editTaskName}>
										 	<div className='form-group'>
												<input
													className='form-control form-control m-2'
										            type="text" 
										            value={this.state.title}
										            onChange={event => this.setState({ title: event.target.value })}
										            placeholder= {this.state.title}
										            required 
										        />
										        <select className="form-select" onChange={event => this.setState({ theme: event.target.value })}>
												  <option defaultValue >{this.state.theme}</option>
												  {this.props.themes.map(theme => {
    												return theme.title !== this.state.theme ?
												  	<option key={this.state.theme.id} value={theme.title}>{theme.title}</option>
												  	: <></>
												  }
												   )};
												</select>
											</div>
										 </form>
										<span className='status badge bg-secondary mx-3'>{this.state.status}</span>
									</div>
								</div> )
						}
						</div>
	}
}

export default Task;
