import React from 'react'
import { Form } from "react-bootstrap";
import { Droppable, Draggable } from 'react-beautiful-dnd';
import './backlog.css';
import AddItem from './../Forms/AddItem.js'
import Task from './../Task/Task.js';



class AddTask extends AddItem {
	addItem = (event) => {
		event.preventDefault()
		console.log('addTask')
	}

	cta = 'Add Task'
    formGroups =  <Form.Group className='col-10 mx-auto'>
					<Form.Control placeholder="Task Name" type="text" onChange={event => this.setState({ newTitle: event.target.value })}/>
				  </Form.Group>

}

class Backlog extends React.Component {
	render() {
		return  <div className='backlog-container col-4'>
				  <div className='component-container'>
				  	<div className='component-title'>
				  		<span>Backlog</span>
				  	</div>
				  	<AddTask />
				  	<Droppable droppableId={'Backlog'}>
						{(provided) => (
							<div 
								ref={provided.innerRef} 
							>
						  	{this.props.tasks.map(
						  		(task, index) => 
						  		task.backlog == 'backlog' &&
							  		<Draggable key={task.id} draggableId={String(task.id)} index={index}>
										{provided => (

											<div  
												ref={provided.innerRef}
												{...provided.draggableProps}
				      					        {...provided.dragHandleProps}		
						    			    >
								  				<Task 
													themes={this.props.themes}
													epics={this.props.epics}
													allTimeboxes={this.props.allTimeboxes}
													deleteTask={this.props.deleteTask}
													{...task}
												/>
											</div>
										)}
									</Draggable>

						  	)}


						  	{provided.placeholder}
						  	</div>
						  	)}
					</Droppable>
				  </div>
				</div>
	}
}

export default Backlog
