import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import pencil from './pencil.svg' 

const Button = (props) => {
	return 	<button className={props.styling} onClick={props.action}>{props.body}</button>

}

class Task extends React.Component {
	state = {id: this.props.id, title: this.props.title, status:this.props.status, priority: this.props.priority, editing:false}
	//these three methods are doing the same thing, make them one function
	markAsDone = () => {
		const prev_status = this.state.status
		const new_status = 'Done'
		this.props.updateStatus(this.state.id, new_status)
    this.setState({status: new_status})
		axios.post('/update_task_status', {
		  id: this.state.id,
		  from_status: prev_status,
		  to_status: new_status
		});
	}

	markAsInProgress = () => {
		this.props.updateStatus(this.state.id, 'In Progress')
    this.setState({status: 'In Progress'})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: 'In Progress'
		});
	}

	reopen = () => {
		this.props.updateStatus(this.state.id, 'To Do')
    this.setState({status: 'To Do'})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: 'To Do'
		});
	}

	edit = () => {
		this.setState({editing: true})
	}

	save = (event) => {
		this.setState({editing: false})
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: this.state.status
		});
	}

	editTaskName = (event) => {
		event.preventDefault()
		this.setState({editing: false})
		this.props.editTaskName(this.state.id, this.state.title)
		axios.post('/edit_task', {
		  id: this.state.id,
		  title: this.state.title,
		  status: this.state.status

		});
	}

	render() {
		return <div className='card m-2'>
							{this.state.editing === false ? (
							<div className='card-body'>
									<img alt="edit-pencil" className='edit-pencil' onClick={this.edit} src={pencil}></img>
								<div className='card-title'>
									{this.state.title}
									<span className='status badge bg-secondary mx-3'>{this.state.status}</span>
								</div>

								{ this.state.status === 'To Do' ? (
									<Button action={this.markAsInProgress} body='Start Work' styling='btn btn-success' />
									) : this.state.status === 'In Progress' ? (
									<Button action={this.markAsDone} body='Mark as Done' styling='btn btn-primary'/>
									) : this.state.status === 'Done' ? (
									<Button action={this.reopen} body='Reopen' styling='btn btn-secondary'/>
									)
									: (
									<div></div>
									)


							}
							</div> ) : (
											<div className='card-body'>
												<Button action={this.save} body='save' styling='btn btn-primary'/>
											<div className='card-title'>
												<form onSubmit={this.editTaskName}>
												 	<div className='form-group'>
														<input
															className='form-control form-control m-2'
										          type="text" 
										          value={this.state.title}
										          onChange={event => this.setState({ title: event.target.value })}
										          placeholder= {this.state.title}
										          required 
										        />
										        </div>
												 </form>
												<span className='status badge bg-secondary mx-3'>{this.state.status}</span>
											</div>



							</div>
											)

						}
						</div>
	}
}

class TasksColumn extends React.Component {
	sortAndMapTasks = () => {
		this.props.tasks.sort(a => a.priority);
	}
	render() {
		return <div className='col-4'>
							<div className='row mt-2'>
								<div className='col-6 mx-auto w-100'>
									{this.props.tasks.map(task => 
										<Task key={task.id} 
													updateStatus={this.props.updateStatus}
													editTaskName={this.props.editTaskName} 
													{...task}
													/>)
									}
								</div>
							</div>
						</div>
	}

}

class TaskList extends React.Component {
	state = {allTasks : [], taskSplittingComplete: false};

	updateStatus = (id, status) => {
		const obj = this.state.allTasks.find(o => o.id === id);
		obj['status'] = status
		//should probably set state of all_tasks here too. Actually just setting all tasks might be the way to go here
		if (status === 'Done') {
			const newInProgressTasks = this.state.inProgressTasks.filter(task => task !== obj)
			this.setState({inProgressTasks: newInProgressTasks})
			this.setState({doneTasks: [...this.state.doneTasks, obj]})
		} else if (status === 'In Progress'){
			const newToDoTasks = this.state.toDoTasks.filter(task => task !== obj)
			this.setState({toDoTasks: newToDoTasks})
			this.setState({inProgressTasks: [...this.state.inProgressTasks, obj]})
		} 
		else if (status === 'To Do'){
			const newDoneTasks = this.state.doneTasks.filter(task => task !== obj)
			this.setState({doneTasks: newDoneTasks})
			this.setState({toDoTasks: [...this.state.toDoTasks, obj]})
		}
	}

	editTaskName = (id, title) => {
		const obj = this.state.allTasks.find(o => o.id === id);
		const newAllTasks = this.state.allTasks.filter(task => task !== obj)
		obj['title'] = title
		this.setState({allTasks: [...newAllTasks, obj]})
		//post to server 

	}

	getTasks = async () => {
    const resp = await axios.get(`/get_tasks`);
    this.setState({ allTasks: resp.data['tasks'] });
  };

  splitTasksByStatus = () => {
  	const doneOnly = this.state.allTasks.filter(task => task.status === 'Done');
  	const inProgressOnly = this.state.allTasks.filter(task => task.status === 'In Progress'); 
  	const toDoOnly = this.state.allTasks.filter(task => task.status === 'To Do');
  	this.setState({ doneTasks: doneOnly });
  	this.setState({ inProgressTasks: inProgressOnly });
  	this.setState({ toDoTasks: toDoOnly})
  	this.setState({ taskSplittingComplete: true})
  }

  async componentDidMount() {
    	await this.getTasks();
    	await this.splitTasksByStatus()

    }

	render() {
		//to-do create a component for this column and call that
		//to-do find out if your taskSplittingComplete is a hack or legit
		return <div className='row'>


						{ this.state.taskSplittingComplete === true ? (
						<React.Fragment>
						<TasksColumn tasks={this.state.toDoTasks}  updateStatus={this.updateStatus} editTaskName={this.editTaskName}/>
						<TasksColumn tasks={this.state.inProgressTasks}  updateStatus={this.updateStatus} editTaskName={this.editTaskName}/>
						<TasksColumn tasks={this.state.doneTasks}  updateStatus={this.updateStatus} editTaskName={this.editTaskName}/>
						</React.Fragment>
						) : (
									<div></div>
								)}

					</div>

	}
}

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
    const data = {title: this.state.title}
    await fetch('/add_task', {
	    method: 'POST', // 
	    mode: 'cors', // 
	    cache: 'no-cache', // 
	    credentials: 'same-origin', 
	    headers: {
	      'Content-Type': 'application/json'
	    },
	    redirect: 'follow', 
	    referrerPolicy: 'no-referrer',
	    body: JSON.stringify(data)
  });
    this.setState({ title: '' });
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto'>Add Task</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class Header extends React.Component {
	render() {
		return <div className='col-12 header text-center'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

class App extends React.Component {
		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...this.state.tasks, taskData],
    }));
  };

	render() {
		return <div className='row'>
						<Header />
						<CreateTaskForm onSubmit={this.addNewTask}/>
						<TaskList />
					 </div>
	}
}


export default App;
