import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import { NavLink } from 'react-router-dom';

class ModeSwitcher extends React.Component {
	render() {
		if (this.props.mode === 'planning') {

			return  <ul className="nav row mode-switcher">
						<li className='col-2 text-center '>
							<NavLink to={{pathname: `/`}}>Home</NavLink>
						</li>
						<li className='col-5 text-center selected'>
							<NavNavLink to={{pathname: `/planning/${this.props.projectId}`}}>Planning</NavNavLink>
						</li>
						<li className='nav-item col-5 text-center'>
							<NavLink to={{pathname: `/working/${this.props.projectId}`}}>Working</NavLink>
						</li>
					</ul>
		}

		else {

			return  <ul className="nav row mode-switcher">
					<li className='col-2 text-center'>
							<NavLink to={{pathname: `/`}}>Home</NavLink>
					</li>
					<li className='col-5 text-center'>
							<NavLink to={{pathname: `/planning/${this.props.projectId}`}}>Planning</NavLink>
					</li>
					<li className='nav-item col-5 text-center selected'>
							<NavLink to={{pathname: `/working/${this.props.projectId}`}}>Working</NavLink>
					</li>
				</ul>

		}
	}
}

export default ModeSwitcher
