import React from 'react';
import 'bootstrap';
import axios from 'axios';
import './App.css';
import Task from './task.js'

class AddElement extends React.Component {
	form = (action, title) => {
		return <form className='add-theme-form' onSubmit={action}>
										 	<div className='form-group'>
												<input
													className='form-control form-control-sm m-2'
								          type="text" 
								          value={title}
								          onChange={event => this.setState({ title: event.target.value })}
								          placeholder="Add New theme" 
								          required 
								        />
								        </div>
										 </form>
		}

	render() {
						return this.form()

	}
}

class AddTheme extends AddElement{
	state = {title: ''}

	addTheme = async (event) => {
		await axios.post('/add_theme', {
		  title: this.state.title
		});
	}

	render() {
		return this.form(this.title, this.addTheme)
	}
}

class Theme extends React.Component {
	render() {
		return <div className='theme-button' onClick = {() => this.props.filterByStatus(this.props.themeTitle)} style={{backgroundColor: this.props.color}}>{this.props.themeTitle}</div>
	}
}

const ClearFilters = (props) => {
	return <div className='theme-button clear' onClick={() => props.filterByStatus('')}>clear</div>
}

class ThemeRow extends React.Component {
	state = {filtering: false}

	filterByStatus = (status) => {
		this.props.filterByStatus(status);
		if (status === '') {
			this.setState({filtering: false})
		} else {
			this.setState({filtering: true})
	  }
	}

	render() {
		return <div className='row'>
								{this.props.themes !== [] ?  (
								 <div className='col-12'>
									{this.props.themes.map(theme => 
										<Theme key={theme.id} themeTitle={theme.title} color={theme.color} filterByStatus={this.filterByStatus}/>)}
										{this.state.filtering === true ? (
											<ClearFilters filterByStatus={this.filterByStatus}/>) : <div></div> }
										<AddTheme />
								 </div> )
								: <div></div> }						 	
					 </div>

	}

}

class TasksColumn extends React.Component {
	state = {tasks: []}


	render() {
		const tasks = [...this.props.tasks].sort((a,b) => a.priority > b.priority ? 1 : -1)
		return <div className='col-4'>
							<div className='row mt-2'>
								<div className='col-6 mx-auto w-100'>
									{tasks.map(task => 
										<Task key={task.id}
													last={task.priority === tasks.length -1 ? true : false}
										 			updateStatus={this.props.updateStatus}
													changePriority={this.props.changePriority}
													themes={this.props.themes}
													{...task}
													/>)
									}
								</div>
							</div>
						</div>
	}
}

const TaskList = (props) => {
  	const doneOnly = props.tasks.filter(task => task.status === 'Done');
 		const inProgressOnly = props.tasks.filter(task => task.status === 'In Progress'); 
  	const toDoOnly = props.tasks.filter(task => task.status === 'To Do');


		return <div className='row'>
						<React.Fragment>
						<TasksColumn tasks={toDoOnly}  changePriority={props.changePriority} updateStatus={props.updateStatus} themes={props.themes}/>
						<TasksColumn tasks={inProgressOnly}  changePriority={props.changePriority} updateStatus={props.updateStatus} themes={props.themes}/>
						<TasksColumn tasks={doneOnly}  changePriority={props.changePriority} updateStatus={props.updateStatus} themes={props.themes}/>
						</React.Fragment>
					</div>


}

class CreateTaskForm extends React.Component {
	state = { title: ''};
	handleSubmit = async (event) => {
    const data = {title: this.state.title}
    await fetch('/add_task', {
	    method: 'POST', // 
	    mode: 'cors', // 
	    cache: 'no-cache', // 
	    credentials: 'same-origin', 
	    headers: {
	      'Content-Type': 'application/json'
	    },
	    redirect: 'follow', 
	    referrerPolicy: 'no-referrer',
	    body: JSON.stringify(data)
  });
    this.setState({ title: '' });
  };
	render () {
		return <div className='row'>
						<div className='col-6 mx-auto mt-3'>
						 <form onSubmit={this.handleSubmit}>
						 	<div className='form-group'>
								<input
									className='form-control form-control-lg m-2'
				          type="text" 
				          value={this.state.title}
				          onChange={event => this.setState({ title: event.target.value })}
				          placeholder="What do you need to get done?" 
				          required 
				        />
				        </div>
				        <div className='col text-center'>
									<button className='btn btn-primary mx-auto mb-4'>Add Task</button>
								</div>
						 </form>
						 </div>
						</div>

	}
}

class Header extends React.Component {
	render() {
		return <div className='col-12 header text-center'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

class App extends React.Component {
		state = {data_received:false}

		addNewTask = (taskData) => {
  	this.setState(prevState => ({
    	tasks: [...this.state.tasks, taskData],
    }));
  };

  	changePriority = async (task_id, direction) => {
  		await axios.post('/update_priority', {
  			task_id: task_id,
  			priority: direction
  		})
  		this.getTasks()
  	}

  	filterByStatus = async (status) => {
  			if (status === '') {
  				this.getTasks()
  			} else {
  			await this.getTasks()
	  		const filteredTasks = this.state.tasks.filter(task => task.theme === status);
	  		this.setState({tasks: filteredTasks});
	  		}

  	}

  	getTasks = async () => {
    const resp = await axios.get(`/get_tasks`);
    await this.setState({ tasks: resp.data['tasks'], themes:resp.data['themes'] });
  };

  	updateStatus = (id, new_status) => {
  		const obj = this.state.tasks.find(o => o.id === id);
  		obj.status = new_status
  		const all_tasks = this.state.tasks.filter(task => task !== obj)
  		this.setState(prevState => ({
    	tasks: [...all_tasks, obj],
    	}));
  	}




   	async componentDidMount() {
    	await this.getTasks();
    	this.setState({data_received: true})

    }

	render() {
		return this.state.data_received === true ? (
						<div className='row'>
						<Header />
						<CreateTaskForm onSubmit={this.addNewTask}/>
						<ThemeRow filterByStatus={this.filterByStatus} themes={this.state.themes}/>
						<TaskList tasks={this.state.tasks} themes={this.state.themes} updateStatus={this.updateStatus} changePriority={this.changePriority}/>
					 </div> )
					: <div></div>

	}
}


export default App;
