import React, {useState} from 'react'
import axios from 'axios';


class AddItem extends React.Component {

	render () {
		return  <Form onSubmit = {this.addItem} >
					<div className="row mb-3">
						{this.formGroups}
					</div>
					<div className='row'>
						<div className='col-6 mx-auto text-center'>
							<input className='btn btn-primary mb-2' type="submit" value={this.cta}/>
						</div>
					</div>
				</Form>
	}
}

const AddThemee = ({ onSubmit }) => {
  const [title, setTitle] = useState('');

  const addTheme = async (event) => {
  	event.preventDefault()
	axios.post('/add_theme', {
	  title: this.state.title,
	  project_id: this.props.projectId
	  })
	.then((response) => {
	  props.addTheme(response.data)
	}, (error) => {
	  console.log(error);
	});
	setTitle('')
	}

  return (
    <Form
      onSubmit={addTheme}
      cta='Add Themee'
    >
      <FullWidthInputField value={title} onChange={setTitle}>
      </FullWidthInputField>

    </Form>
  );
};

const Form = ({ onSubmit, cta, children }) => (
  <form onSubmit={onSubmit}>
    <div className="row mb-3">
		{children}
	</div>
	<div className='row'>
		<div className='col-6 mx-auto text-center'>
			<input className='btn btn-primary mb-2' type="submit" value={cta}/>
		</div>
	</div>
  </form>
);


const FullWidthInputField = ({ value, onChange, children }) => (
    <div className='col-10 mx-auto'>
		<input className='form-control' placeholder="Theme Name" value={value} type="text" onChange={event => onChange(event.target.value)}/>
	</div>
);

export {AddThemee, AddItem}
