const TaskNameInput = (props) => {
		return <input placeholder='Task Name'></input>
	}


class CreateTask extends React.Component {
	render (props) {
		return <form onSubmit={this.props.addTask}>
						<TaskNameInput />
						<button>Add Task</button>
					 </form>
	}
}

class Header extends React.Component {
	render() {
		return <div className='header'>
						 <div className='title'>Todoodleoo</div>
						 <div className='subtitle'>Smart to-do list app</div>
						</div>

	}
}

class App extends React.Component {

	render() {
		const handleAddTask = async (event) => {
			event.preventDefault();
			const requestOptions = {
					method: 'POST',
					headers: { 'Content-Type': 'application/json' },
					body: JSON.stringify({ title: 'React POST Request Example' })
					};
			const response = await fetch('/add_task', requestOptions);
			const data = await response.json();
			console.log(data)
	};
		return <div>
						<Header />
						<CreateTask addTask={handleAddTask}/>
					 </div>
	}

}

export default App;
