import React from 'react';
import axios from 'axios';
import Board from './board.js';

class BoardsList extends React.Component {
	render() {
		console.log(this.props.boards)
		return 	<div>
							<h1> Boards </h1>
							{this.props.boards[0]['board_title']}

						</div>
	}
}

class App extends React.Component {
	state = {boards: []}

	getBoards = async () => {
    const resp = await axios.get(`/get_boards`)
    const boardsList = resp.data['boards']
    this.setState({boards: [boardsList]});
  };

  componentDidMount() {
    this.getBoards();
  }

	render() {
		if (this.state.boards === []){
			return <div>loading</div>
		}

		return <BoardsList boards={this.state.boards}/>

	}
}


export default App;
